SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_pagador_fin ( nr_seq_pagador_p bigint, ie_opcao_p text, dt_referencia_p timestamp default clock_timestamp()) RETURNS varchar AS $body$
DECLARE

/*
	D - Dias de vencimento
	M - Codigo da matricula
	E - Nome da empresa
	A -  Agencia
	DA -  Digito agencia
	DC -  Digito conta
	C - Conta
	AC - Agencia e Conta
	TP - Tipo portador
	CP - Codigo do portador
	F - Forama cobranca
	NS - Retorna nr_seq_conta_banco
	SEQ - Sequencia
	CC - Carteira cobranca
	CB - Codigo do banco
*/
	
			
ds_retorno_w			varchar(4000);
nr_seq_fin_pagador_w		bigint;
nr_seq_conta_banco_deb_aut_w	bigint;
nr_seq_conta_banco_w		banco_estabelecimento.nr_sequencia%type;
cd_banco_w			banco_estabelecimento.cd_banco%type;


BEGIN
select	max(nr_sequencia)
into STRICT	nr_seq_fin_pagador_w
from	pls_contrato_pagador_fin
where	nr_seq_pagador	= nr_seq_pagador_p
and	trunc(dt_referencia_p) between trunc(dt_inicio_vigencia,'dd') and fim_dia(coalesce(dt_fim_vigencia,dt_referencia_p));

if (coalesce(nr_seq_fin_pagador_w,0) <> 0) then
	if (ie_opcao_p = 'D') then
		select	to_char(dt_dia_vencimento)
		into STRICT	ds_retorno_w
		from	pls_contrato_pagador_fin
		where	nr_sequencia = nr_seq_fin_pagador_w;
	elsif (ie_opcao_p = 'M') then
		select	cd_matricula
		into STRICT	ds_retorno_w
		from	pls_contrato_pagador_fin
		where	nr_sequencia = nr_seq_fin_pagador_w;
	elsif (ie_opcao_p = 'E') then
		select	max(substr(pls_obter_dados_empresa(nr_seq_empresa,'D'),1,250))
		into STRICT	ds_retorno_w
		from	pls_contrato_pagador_fin
		where	nr_sequencia = nr_seq_fin_pagador_w;
	elsif (ie_opcao_p = 'A') then
		select	max(cd_agencia_bancaria)
		into STRICT	ds_retorno_w
		from	pls_contrato_pagador_fin
		where	nr_sequencia = nr_seq_fin_pagador_w;
	elsif (ie_opcao_p = 'C') then
		select	max(cd_conta)
		into STRICT	ds_retorno_w
		from	pls_contrato_pagador_fin
		where	nr_sequencia = nr_seq_fin_pagador_w;
	elsif (ie_opcao_p = 'AC') then
		select (cd_agencia_bancaria || '/' ||cd_conta)
		into STRICT	ds_retorno_w
		from	pls_contrato_pagador_fin
		where	nr_sequencia	= nr_seq_fin_pagador_w;
		
		if (ds_retorno_w = '/') then
			select	coalesce(nr_seq_conta_banco, nr_seq_conta_banco_deb_aut)
			into STRICT	nr_seq_conta_banco_w
			from	pls_contrato_pagador_fin
			where	nr_sequencia	= nr_seq_fin_pagador_w;
			
			select	max(cd_agencia_bancaria || '/' ||cd_conta)
			into STRICT	ds_retorno_w
			from	banco_estabelecimento
			where	nr_sequencia = nr_seq_conta_banco_w;
		end if;
	elsif (ie_opcao_p = 'TP') then
		select	cd_tipo_portador
		into STRICT	ds_retorno_w
		from	pls_contrato_pagador_fin
		where	nr_sequencia = nr_seq_fin_pagador_w;
	elsif (ie_opcao_p = 'CP') then
		select	cd_portador
		into STRICT	ds_retorno_w
		from	pls_contrato_pagador_fin
		where	nr_sequencia = nr_seq_fin_pagador_w;
	elsif (ie_opcao_p = 'F') then
		select	nr_seq_forma_cobranca
		into STRICT	ds_retorno_w
		from	pls_contrato_pagador_fin
		where	nr_sequencia = nr_seq_fin_pagador_w;
	elsif (ie_opcao_p = 'DA') then
		select	ie_digito_agencia
		into STRICT	ds_retorno_w
		from	pls_contrato_pagador_fin
		where	nr_sequencia = nr_seq_fin_pagador_w;	
	elsif (ie_opcao_p = 'DC') then
		select	ie_digito_conta
		into STRICT	ds_retorno_w
		from	pls_contrato_pagador_fin
		where	nr_sequencia = nr_seq_fin_pagador_w;
	elsif (ie_opcao_p = 'NS') then
		select	nr_seq_conta_banco
		into STRICT	ds_retorno_w
		from	pls_contrato_pagador_fin
		where	nr_sequencia = nr_seq_fin_pagador_w;
	elsif (ie_opcao_p = 'SEQ') then
		ds_retorno_w	:= nr_seq_fin_pagador_w;
	elsif (ie_opcao_p = 'NSD') then
		select	nr_seq_conta_banco_deb_aut
		into STRICT	ds_retorno_w
		from	pls_contrato_pagador_fin
		where	nr_sequencia = nr_seq_fin_pagador_w;	
	elsif (ie_opcao_p = 'CPD') then
		select	cd_portador_deb_aut
		into STRICT	ds_retorno_w
		from	pls_contrato_pagador_fin
		where	nr_sequencia = nr_seq_fin_pagador_w;
	elsif (ie_opcao_p = 'AD') then
		select	nr_seq_conta_banco_deb_aut
		into STRICT	nr_seq_conta_banco_deb_aut_w
		from	pls_contrato_pagador_fin
		where	nr_sequencia = nr_seq_fin_pagador_w;	
	
		select	max(cd_agencia_bancaria)
		into STRICT	ds_retorno_w
		from	banco_estabelecimento
		where	nr_sequencia	= nr_seq_conta_banco_deb_aut_w;
	elsif (ie_opcao_p = 'CD') then
		select	nr_seq_conta_banco_deb_aut
		into STRICT	nr_seq_conta_banco_deb_aut_w
		from	pls_contrato_pagador_fin
		where	nr_sequencia = nr_seq_fin_pagador_w;	
		
		select	max(cd_conta)
		into STRICT	ds_retorno_w
		from	banco_estabelecimento
		where	nr_sequencia	= nr_seq_conta_banco_deb_aut_w;
	elsif (ie_opcao_p = 'DCD') then
		select	nr_seq_conta_banco_deb_aut
		into STRICT	nr_seq_conta_banco_deb_aut_w
		from	pls_contrato_pagador_fin
		where	nr_sequencia = nr_seq_fin_pagador_w;	
	
		select	max(ie_digito_conta)
		into STRICT	ds_retorno_w
		from	banco_estabelecimento
		where	nr_sequencia	= nr_seq_conta_banco_deb_aut_w;
	elsif (ie_opcao_p = 'CBD') then
		select	nr_seq_conta_banco_deb_aut
		into STRICT	nr_seq_conta_banco_deb_aut_w
		from	pls_contrato_pagador_fin
		where	nr_sequencia = nr_seq_fin_pagador_w;	
	
		select	max(cd_banco)
		into STRICT	ds_retorno_w
		from	banco_estabelecimento
		where	nr_sequencia	= nr_seq_conta_banco_deb_aut_w;
	elsif (ie_opcao_p = '') then
		select	nr_seq_conta_banco_deb_aut
		into STRICT	nr_seq_conta_banco_deb_aut_w
		from	pls_contrato_pagador_fin
		where	nr_sequencia = nr_seq_fin_pagador_w;	
	elsif (ie_opcao_p = 'CC') then
		select	nr_seq_carteira_cobr
		into STRICT	ds_retorno_w
		from	pls_contrato_pagador_fin
		where	nr_sequencia = nr_seq_fin_pagador_w;	
	elsif (ie_opcao_p = 'CB') then		
		select	coalesce(nr_seq_conta_banco, nr_seq_conta_banco_deb_aut)
		into STRICT	nr_seq_conta_banco_w
		from	pls_contrato_pagador_fin
		where	nr_sequencia = nr_seq_fin_pagador_w;
		
		if (coalesce(nr_seq_conta_banco_w,0) <> 0) then
			select	cd_banco
			into STRICT	cd_banco_w
			from	banco_estabelecimento
			where	nr_sequencia = nr_seq_conta_banco_w;
		else
			select	cd_banco
			into STRICT	cd_banco_w
			from	pls_contrato_pagador_fin
			where	nr_sequencia = nr_seq_fin_pagador_w;
		end if;
	end if;	
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_dados_pagador_fin ( nr_seq_pagador_p bigint, ie_opcao_p text, dt_referencia_p timestamp default clock_timestamp()) FROM PUBLIC;

