CREATE OR REPLACE FUNCTION pls_obter_dados_pagador_prop ( nr_seq_pagador_proposta_p pls_proposta_pagador.nr_sequencia%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* ie_opcao_p
	N = Nome pagador
*/
ds_retorno_w	varchar(255);
nm_pagador_w	varchar(255);


BEGIN

select	substr((select	obter_nome_pf(x.cd_pessoa_fisica)
		from	pessoa_fisica x
		where	x.cd_pessoa_fisica = a.cd_pagador
		
UNION ALL

		select	y.ds_razao_social
		from	pessoa_juridica y
		where	y.cd_cgc	= a.cd_cgc_pagador),1,255)
into STRICT	nm_pagador_w
from	pls_proposta_pagador	a
where	a.nr_sequencia = nr_seq_pagador_proposta_p;

if (ie_opcao_p = 'N') then
	ds_retorno_w := nm_pagador_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
