SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_pagador ( nr_seq_pagador_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:   Obter dados do pagador
----------------------------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ x ] Tasy (Delphi/Java) [ x ] Portal [  ] Relatórios [ ] Outros:
 ----------------------------------------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
	IE_OPCAO_P

	* N  - Nome do pagador
	* D  - Nome e descrição do pagador
	* T  - Tipo do pagador
	* NC - Nr contrato
	* S  - Status
	* CI - Contrato intercâmbio
	* SC - Sequência contrato
	* OE - Cód. operadora empresa
	* NF - Nome fantasia
	* CPF - CPF
	* CGC - CGC
	* M - E-mail
	* TE - Tipo de endereço cadastrado  no contrato
	* CPFP - Código pessoa fisica do pagador
	* TEP - Tipo endereço pagador
	*TEC - Tipo Envio Cobrança
	* NA - Notificação de atraso
*/nm_pagador_w		varchar(255);
ds_pagador_w		varchar(255);
ds_retorno_w		varchar(255);
cd_cgc_w		varchar(14);
nr_cpf_w		varchar(14);
cd_pessoa_fisica_w	varchar(10);
nr_contrato_w		bigint;
nr_seq_contrato_w	bigint;


BEGIN

if (ie_opcao_p = 'N') then
	select	substr(obter_nome_pf_pj(a.cd_pessoa_fisica, a.cd_cgc),1,255)
	into STRICT	nm_pagador_w
	from	pls_contrato_pagador	a
	where	a.nr_sequencia		= nr_seq_pagador_p;

	ds_retorno_w	:= nm_pagador_w;
elsif (ie_opcao_p = 'D') then
	select	substr(obter_nome_pf_pj(a.cd_pessoa_fisica, a.cd_cgc),1,255),
		a.ds_pagador
	into STRICT	nm_pagador_w,
		ds_pagador_w
	from	pls_contrato_pagador a
	where	a.nr_sequencia = nr_seq_pagador_p;

	if ((trim(both ds_pagador_w) IS NOT NULL AND (trim(both ds_pagador_w))::text <> '')) then
		ds_retorno_w	:= nm_pagador_w || ' - ' || ds_pagador_w;
	else
		ds_retorno_w	:= nm_pagador_w;
	end if;
elsif (ie_opcao_p = 'T') then
	select	a.cd_pessoa_fisica,
		a.cd_cgc
	into STRICT	cd_pessoa_fisica_w,
		cd_cgc_w
	from	pls_contrato_pagador	a
	where	a.nr_sequencia		= nr_seq_pagador_p;

	if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then
		ds_retorno_w	:= 'PF';
	elsif (cd_cgc_w IS NOT NULL AND cd_cgc_w::text <> '') then
		ds_retorno_w	:= 'PJ';
	end if;
elsif (ie_opcao_p = 'CPF') then
	select	a.cd_pessoa_fisica
	into STRICT	cd_pessoa_fisica_w
	from	pls_contrato_pagador	a
	where	a.nr_sequencia		= nr_seq_pagador_p;

	if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then
		select	max(nr_cpf)
		into STRICT	nr_cpf_w
		from	pessoa_fisica
		where	cd_pessoa_fisica	= cd_pessoa_fisica_w;
		ds_retorno_w	:= nr_cpf_w;
	end if;
elsif (ie_opcao_p = 'CGC') then
	select	a.cd_cgc
	into STRICT	cd_cgc_w
	from	pls_contrato_pagador	a
	where	a.nr_sequencia		= nr_seq_pagador_p;

	if (cd_cgc_w IS NOT NULL AND cd_cgc_w::text <> '') then
		ds_retorno_w	:= cd_cgc_w;
	end if;
elsif (ie_opcao_p = 'NC') then
	select	b.nr_contrato
	into STRICT	nr_contrato_w
	FROM pls_contrato_pagador a
LEFT OUTER JOIN pls_contrato b ON (a.nr_seq_contrato = b.nr_sequencia)
WHERE a.nr_sequencia		= nr_seq_pagador_p;

	ds_retorno_w	:= nr_contrato_w;
elsif (ie_opcao_p = 'S') then
	select	CASE WHEN count(1)=0 THEN  null  ELSE 'R' END
	into STRICT	ds_retorno_w
	from	pls_contrato_pagador
	where	(dt_rescisao IS NOT NULL AND dt_rescisao::text <> '')
	and	nr_sequencia	= nr_seq_pagador_p;

	if (coalesce(ds_retorno_w::text, '') = '') then
		select	CASE WHEN count(1)=0 THEN  null  ELSE 'S' END
		into STRICT	ds_retorno_w
		from	pls_contrato_pagador
		where	(dt_suspensao IS NOT NULL AND dt_suspensao::text <> '')
		and	nr_sequencia	= nr_seq_pagador_p;
	end if;
elsif (ie_opcao_p = 'CI') then
	select	max(a.nr_sequencia)
	into STRICT	ds_retorno_w
	from	pls_contrato_pagador	b,
		pls_intercambio		a
	where	a.nr_sequencia	= b.nr_seq_pagador_intercambio
	and	b.nr_sequencia	= nr_seq_pagador_p;
elsif (ie_opcao_p = 'SC') then
	select	b.nr_sequencia
	into STRICT	nr_seq_contrato_w
	FROM pls_contrato_pagador a
LEFT OUTER JOIN pls_contrato b ON (a.nr_seq_contrato = b.nr_sequencia)
WHERE a.nr_sequencia		= nr_seq_pagador_p;

	ds_retorno_w	:= nr_seq_contrato_w;
elsif (ie_opcao_p = 'OE') then
	select	max(a.cd_operadora_empresa)
	into STRICT	ds_retorno_w
	from	pls_contrato_pagador	b,
		pls_intercambio		a
	where	a.nr_sequencia	= b.nr_seq_pagador_intercambio
	and	b.nr_sequencia	= nr_seq_pagador_p;

	if (coalesce(ds_retorno_w::text, '') = '') then
		select	max(a.cd_operadora_empresa)
		into STRICT	ds_retorno_w
		from	pls_contrato_pagador	b,
			pls_contrato		a
		where	a.nr_sequencia	= b.nr_seq_contrato
		and	b.nr_sequencia	= nr_seq_pagador_p;
	end if;
elsif (ie_opcao_p = 'NF') then
	select	cd_pessoa_fisica,
		cd_cgc
	into STRICT	cd_pessoa_fisica_w,
		cd_cgc_w
	from	pls_contrato_pagador
	where	nr_sequencia	= nr_seq_pagador_p;

	if (cd_cgc_w IS NOT NULL AND cd_cgc_w::text <> '') then
		select	substr(a.nm_fantasia,1,80)
		into STRICT	ds_retorno_w
		from	pessoa_juridica	a
		where	a.cd_cgc	= cd_cgc_w;
	else
		ds_retorno_w	:= substr(obter_nome_pf(cd_pessoa_fisica_w),1,255);
	end if;
elsif (ie_opcao_p = 'M') then
	select	coalesce(ds_email,obter_dados_pf_pj(cd_pessoa_fisica,cd_cgc,'M'))
	into STRICT	ds_retorno_w
	from	pls_contrato_pagador
	where	nr_sequencia	= nr_seq_pagador_p;
elsif (ie_opcao_p = 'TE') then
	select	b.ie_endereco_boleto
	into STRICT	ds_retorno_w
	from	pls_contrato_pagador b
	where	b.nr_sequencia = nr_seq_pagador_p;
elsif (ie_opcao_p = 'CPFP') then
	select	a.cd_pessoa_fisica
	into STRICT	ds_retorno_w
	from	pls_contrato_pagador a
	where	a.nr_sequencia = nr_seq_pagador_p;
elsif (ie_opcao_p = 'TEP') then
	select	a.ie_endereco_boleto
	into STRICT	ds_retorno_w
	from	pls_contrato_pagador a
	where	a.nr_sequencia = nr_seq_pagador_p;
elsif (ie_opcao_p = 'TEC') then
	select	max(a.ie_envia_cobranca)
	into STRICT	ds_retorno_w
	from	pls_contrato_pagador a
	where	a.nr_sequencia = nr_seq_pagador_p;
elsif (ie_opcao_p = 'NA') then
	select	obter_valor_dominio(2883,max(a.ie_notificacao))
	into STRICT	ds_retorno_w
	from	pls_contrato_pagador a
	where	a.nr_sequencia = nr_seq_pagador_p;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_dados_pagador ( nr_seq_pagador_p bigint, ie_opcao_p text) FROM PUBLIC;

