CREATE OR REPLACE FUNCTION pls_obter_dados_pag_prestador ( nr_seq_pag_prest_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(255)	:= null;				
 
/*ie_opcao_p 
NP 	- Nome do prestador 
CP	- CÃ³digo do prestador 
*/
				 

BEGIN 
if (nr_seq_pag_prest_p IS NOT NULL AND nr_seq_pag_prest_p::text <> '') then 
	if (ie_opcao_p = 'NP') then 
		select	substr(pls_obter_dados_prestador(a.nr_seq_prestador, 'N'),1,255) 
		into STRICT	ds_retorno_w 
		from	pls_pagamento_prestador a	 
		where	a.nr_sequencia = nr_seq_pag_prest_p;
	elsif (ie_opcao_p = 'CP') then 
		select	substr(pls_obter_cod_prestador(a.nr_seq_prestador,null),1,255) 
		into STRICT	ds_retorno_w 
		from	pls_pagamento_prestador a	 
		where	a.nr_sequencia = nr_seq_pag_prest_p;
	end if;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
