CREATE OR REPLACE FUNCTION pls_obter_dados_pedido_aut_scs ( nr_seq_guia bigint, nr_seq_requisicao_p bigint, nr_seq_pedido_aut_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE

/*
IE_OPCAO_P
1 - Data de atendimento
2 - Prestador de alto custo
*/
ds_retorno_w			varchar(255);


BEGIN

if (ie_opcao_p	= 1) then
	begin
		select	to_char(dt_atendimento, 'dd/mm/yyyy')
		into STRICT	ds_retorno_w
		from	ptu_pedido_autorizacao
		where	nr_seq_requisicao	= nr_seq_requisicao_p;
	exception
	when others then
		ds_retorno_w	:= '';
	end;
elsif (ie_opcao_p	= 2) then
	begin
		select	ie_alto_custo
		into STRICT	ds_retorno_w
		from	ptu_pedido_autorizacao
		where	nr_seq_requisicao	= nr_seq_requisicao_p;
	exception
	when others then
		ds_retorno_w	:= 'N';
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
