SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_pj_compl ( cd_estabelecimento_p bigint, cd_cgc_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* Opcao para dados referentes ao complemento da pessoa juridica
M   -  Email
*/
ie_tipo_compl_w			varchar(3);
ds_email_w			varchar(255);
ds_retorno_w			varchar(254);


BEGIN
select pls_obter_param_padrao_funcao(17,1246)
into STRICT ie_tipo_compl_w
;
if ((ie_tipo_compl_w IS NOT NULL AND ie_tipo_compl_w::text <> '') and ie_tipo_compl_w <> 'X') then
	begin
		select max(c.ds_email)
		into STRICT	ds_email_w
		from    pessoa_juridica a,
			pls_outorgante b,
			pessoa_juridica_compl c
		where   a.cd_cgc = b.cd_cgc_outorgante
		and     a.cd_cgc = c.cd_cgc
		and     b.cd_cgc_outorgante = c.cd_cgc
		and     b.cd_estabelecimento = cd_estabelecimento_p
		and     c.ie_tipo_complemento = ie_tipo_compl_w
		and     (c.ds_email IS NOT NULL AND c.ds_email::text <> '');
	end;
end if;
if (coalesce(ds_email_w::text, '') = '') then
	if (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') and (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') then
		begin
			select	max(ds_email)
			into STRICT	ds_email_w
			from	pessoa_juridica_estab
			where   cd_cgc 			= cd_cgc_p
			and	cd_estabelecimento	= cd_estabelecimento_p;
		end;
	end if;
end if;
if (ie_opcao_p = 'M') then
	begin
	ds_retorno_w := ds_email_w;
	end;
end if;
return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
