SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_portabilidade ( nr_seq_portabilidade_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/* ie_opcao_p
	P - Pessoa da proposta de adesão
	A - Proposta de adesão
	S - SIB
	G - Segurado
*/
ds_retorno_w			varchar(255);
nr_seq_prop_benef_w		bigint;
nr_seq_proposta_w		bigint;
nr_seq_sib_w			bigint;
nr_seq_segurado_w		bigint;


BEGIN

if (ie_opcao_p in ('A','P')) then
	select	max(nr_sequencia)
	into STRICT	nr_seq_prop_benef_w
	from	pls_proposta_beneficiario
	where	nr_seq_portabilidade	= nr_seq_portabilidade_p;

	if (coalesce(nr_seq_prop_benef_w,0) <> 0) then
		if (ie_opcao_p	= 'P') then
			ds_retorno_w	:= to_char(nr_seq_prop_benef_w);
		elsif (ie_opcao_p	= 'A') then
			select	max(nr_seq_proposta)
			into STRICT	nr_seq_proposta_w
			from	pls_proposta_beneficiario
			where	nr_sequencia	= nr_seq_prop_benef_w;

			ds_retorno_w	:= to_char(nr_seq_proposta_w);
		end if;
	end if;
elsif (ie_opcao_p in ('S','G')) then
	select	max(nr_seq_lote_sib)
	into STRICT	nr_seq_sib_w
	from	pls_interf_sib
	where	nr_seq_portabilidade	= nr_seq_portabilidade_p;

	if (coalesce(nr_seq_sib_w,0) <> 0) then
		if (ie_opcao_p	= 'S') then
			ds_retorno_w	:= to_char(nr_seq_sib_w);
		elsif (ie_opcao_p	= 'G') then
			select	max(nr_seq_segurado)
			into STRICT	nr_seq_segurado_w
			from	pls_interf_sib
			where	nr_seq_lote_sib	= nr_seq_sib_w
			and	nr_seq_portabilidade	= nr_seq_portabilidade_p;

			ds_retorno_w	:= to_char(nr_seq_segurado_w);
		end if;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_dados_portabilidade ( nr_seq_portabilidade_p bigint, ie_opcao_p text) FROM PUBLIC;

