CREATE OR REPLACE FUNCTION pls_obter_dados_prestador_web ( nr_seq_usuario_web_p integer, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE

				 
ds_retorno_w			varchar(60) := '';
/* 
 Tipos 
 NP - Nome prestador 
 NU - Nome usu√°rio web 
 PW - Perfil web 
*/
 
 

BEGIN 
 
if (ie_tipo_p = 'NP') then 
	select	substr(coalesce(max(pls_obter_dados_prestador(nr_seq_prestador, 'N')),''),1,60) 
	into STRICT	ds_retorno_w 
	from	pls_usuario_web 
	where	nr_sequencia	= nr_seq_usuario_web_p;
elsif (ie_tipo_p = 'NU') then 
	select	substr(coalesce(max(nm_usuario_web),''),1,60) 
	into STRICT	ds_retorno_w 
	from	pls_usuario_web 
	where	nr_sequencia	= nr_seq_usuario_web_p;
elsif (ie_tipo_p = 'PW') then 
	select	substr(coalesce(max(nr_seq_perfil_web),0),1,60) 
	into STRICT	ds_retorno_w 
	from	pls_usuario_web 
	where	ie_situacao = 'A' 
	and	nr_sequencia = nr_seq_usuario_web_p;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
