CREATE OR REPLACE FUNCTION pls_obter_dados_prest_estab ( nr_seq_prestador_p pls_prestador.nr_sequencia%type, ie_opcao_p text, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) RETURNS varchar AS $body$
DECLARE

 
/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
Finalidade:  Obter dados do prestador restringindo pelo estabelecimento 
---------------------------------------------------------------------------------------------------------------------------------------------------- 
Locais de chamada direta: 
[ ] Objetos do dicionário [ x ] Tasy (Delphi/Java) [ x ] Portal [ ] Relatórios [ ] Outros: 
 ---------------------------------------------------------------------------------------------------------------------------------------------------- 
Pontos de atenção:  
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
 
	N - Nome do prestador 
	CNES - Código nacional de estabelecimento de saúde 
	CD - Código prestador 
*/					 
				 
ds_retorno_w			varchar(4000)	:= null;	
cd_prestador_w			varchar(30);
cd_cgc_w			varchar(50);
cd_pessoa_fisica_w		varchar(10);


BEGIN 
if (ie_opcao_p = 'N') then 
	select	max(a.nm_interno) 
	into STRICT	ds_retorno_w 
	from	pls_prestador a 
	where	nr_sequencia		= nr_seq_prestador_p 
	and	cd_estabelecimento	= cd_estabelecimento_p;
 
	if (coalesce(ds_retorno_w::text, '') = '') then 
		select	max(a.cd_cgc), 
			max(a.cd_pessoa_fisica) 
		into STRICT	cd_cgc_w, 
			cd_pessoa_fisica_w 
		from	pls_prestador a 
		where	a.nr_sequencia		= nr_seq_prestador_p 
		and	a.cd_estabelecimento	= cd_estabelecimento_p;
		 
		if (cd_cgc_w IS NOT NULL AND cd_cgc_w::text <> '')	then 
			select	ds_razao_social 
			into STRICT	ds_retorno_w 
			from	pessoa_juridica 
			where	cd_cgc	= cd_cgc_w;
			 
		elsif (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then 
			select	nm_pessoa_fisica 
			into STRICT	ds_retorno_w 
			from	pessoa_fisica 
			where	cd_pessoa_fisica	= cd_pessoa_fisica_w;
		end if;
	end if;
elsif (ie_opcao_p = 'CNES') then 
	select	substr(obter_dados_pf_pj(cd_pessoa_fisica, cd_cgc, 'CNES'),1,20) 
	into STRICT	ds_retorno_w 
	from	pls_prestador 
	where	nr_sequencia		= nr_seq_prestador_p 
	and	cd_estabelecimento	= cd_estabelecimento_p;
	 
elsif (ie_opcao_p = 'CD') then 
	select	max(a.cd_prestador) 
	into STRICT	cd_prestador_w 
	from	pls_prestador a 
	where	a.nr_sequencia		= nr_seq_prestador_p 
	and	cd_estabelecimento	= cd_estabelecimento_p;
 
	ds_retorno_w	:= cd_prestador_w;
end if;	
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
