SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_prest_relat ( nr_seq_prestador_p bigint, ie_tipo_retorno_p text) RETURNS varchar AS $body$
DECLARE

					 
/* 
Informar o retorno da function - ie_tipo_retorno_p 
B	- Bairro 
C	- Cep 
CO	- Complementos do Endereço 
N	- Nome 
NR	- Número do Endereço 
M	- Município 
R	- Rua 
SG	- Estado 
T	- Telefone 
CGC_PF	- CNPJ/CPF do Prestador 
*/
 
 
 
cd_cep_w			varchar(8);
cd_cgc_w			varchar(14);
cd_pessoa_fisica_w		varchar(10);
ds_bairro_w			varchar(255)	:= '';
ds_complemento_w		varchar(255);
ds_endereco_w			varchar(255);
ds_municipio_w			varchar(40);
ds_retorno_w			varchar(255);
ie_tipo_endereco_w		varchar(5);
nm_prestador_w			varchar(255);
nr_cpf_w			varchar(11);
nr_endereco_w			bigint;

 
nr_seq_compl_adic_w		bigint;
nr_telefone_w			varchar(30);
sg_estado_w			pessoa_juridica.sg_estado%type;

 
 

BEGIN 
 
if (nr_seq_prestador_p IS NOT NULL AND nr_seq_prestador_p::text <> '') then 
	select	a.ie_tipo_endereco, 
		a.cd_pessoa_fisica, 
		a.cd_cgc, 
		coalesce(coalesce(nr_seq_compl_pf_tel_adic,nr_seq_compl_pj),0) 
	into STRICT	ie_tipo_endereco_w, 
		cd_pessoa_fisica_w, 
		cd_cgc_w, 
		nr_seq_compl_adic_w 
	from	pls_prestador a 
	where	a.nr_sequencia	= nr_seq_prestador_p;
		 
	if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then 
		select	a.nm_pessoa_fisica 
		into STRICT	nm_prestador_w 
		from	pessoa_fisica	a, 
			pls_prestador	b 
		where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica 
		and	b.nr_sequencia		= nr_seq_prestador_p;
	elsif (cd_cgc_w IS NOT NULL AND cd_cgc_w::text <> '')then 
		select	a.ds_razao_social 
		into STRICT	nm_prestador_w 
		from	pessoa_juridica	a, 
			pls_prestador	b 
		where	a.cd_cgc	= b.cd_cgc 
		and	b.nr_sequencia	= nr_seq_prestador_p;
	end if;
	 
	if (ie_tipo_endereco_w in ('PJ')) then 
		select	a.ds_bairro, 
			a.nr_telefone, 
			a.cd_cep, 
			a.ds_complemento, 
			a.ds_endereco, 
			a.ds_municipio, 
			a.nr_endereco, 
			a.sg_estado 
		into STRICT	ds_bairro_w, 
			nr_telefone_w, 
			cd_cep_w, 
			ds_complemento_w, 
			ds_endereco_w, 
			ds_municipio_w, 
			nr_endereco_w, 
			sg_estado_w 
		from	pessoa_juridica a 
		where	a.cd_cgc	= cd_cgc_w;
	elsif (ie_tipo_endereco_w in ('PJA')) and (nr_seq_compl_adic_w > 0) then 
		select	a.ds_bairro, 
			a.nr_telefone, 
			a.cd_cep, 
			a.ds_complemento, 
			a.ds_endereco, 
			a.ds_municipio, 
			a.nr_endereco, 
			a.sg_estado 
		into STRICT	ds_bairro_w, 
			nr_telefone_w, 
			cd_cep_w, 
			ds_complemento_w, 
			ds_endereco_w, 
			ds_municipio_w, 
			nr_endereco_w, 
			sg_estado_w 
		from	pessoa_juridica_compl a 
		where	a.cd_cgc	= cd_cgc_w	 
		and	a.nr_sequencia	= nr_seq_compl_adic_w;
	elsif (ie_tipo_endereco_w in ('PJC')) then 
		begin 
		select	a.ds_bairro, 
			a.nr_telefone, 
			a.cd_cep, 
			a.ds_complemento, 
			a.ds_endereco, 
			a.ds_municipio, 
			a.nr_endereco, 
			a.sg_estado 
		into STRICT	ds_bairro_w, 
			nr_telefone_w, 
			cd_cep_w, 
			ds_complemento_w, 
			ds_endereco_w, 
			ds_municipio_w, 
			nr_endereco_w, 
			sg_estado_w 
		from	pessoa_juridica_compl a 
		where	a.cd_cgc	= cd_cgc_w 
		and	a.ie_tipo_complemento = 1;
		exception 
		when others then 
			ds_bairro_w	:= null;
		end;
	elsif (ie_tipo_endereco_w in ('PJF')) then 
		begin 
		select	a.ds_bairro, 
			a.nr_telefone, 
			a.cd_cep, 
			a.ds_complemento, 
			a.ds_endereco, 
			a.ds_municipio, 
			a.nr_endereco, 
			a.sg_estado 
		into STRICT	ds_bairro_w, 
			nr_telefone_w, 
			cd_cep_w, 
			ds_complemento_w, 
			ds_endereco_w, 
			ds_municipio_w, 
			nr_endereco_w, 
			sg_estado_w 
		from	pessoa_juridica_compl a 
		where	a.cd_cgc	= cd_cgc_w 
		and	a.ie_tipo_complemento = 2;
		exception 
		when others then 
			ds_bairro_w	:= null;
		end;
	elsif (ie_tipo_endereco_w = 'PFR') then 
		select	a.ds_bairro, 
			a.nr_telefone, 
			a.cd_cep, 
			a.ds_complemento, 
			a.ds_endereco, 
			a.ds_municipio, 
			a.nr_endereco, 
			a.sg_estado 
		into STRICT	ds_bairro_w, 
			nr_telefone_w, 
			cd_cep_w, 
			ds_complemento_w, 
			ds_endereco_w, 
			ds_municipio_w, 
			nr_endereco_w, 
			sg_estado_w 
		from	compl_pessoa_fisica a 
		where	a.cd_pessoa_fisica	= cd_pessoa_fisica_w 
		and	a.ie_tipo_complemento = 1;
	elsif (ie_tipo_endereco_w = 'PFC') then 
		select	a.ds_bairro, 
			a.nr_telefone, 
			a.cd_cep, 
			a.ds_complemento, 
			a.ds_endereco, 
			a.ds_municipio, 
			a.nr_endereco, 
			a.sg_estado 
		into STRICT	ds_bairro_w, 
			nr_telefone_w, 
			cd_cep_w, 
			ds_complemento_w, 
			ds_endereco_w, 
			ds_municipio_w, 
			nr_endereco_w, 
			sg_estado_w 
		from	compl_pessoa_fisica a 
		where	a.cd_pessoa_fisica	= cd_pessoa_fisica_w 
		and	a.ie_tipo_complemento = 2;
		 
		if (nr_seq_compl_adic_w > 0) then 
			select	a.ds_bairro, 
				a.nr_telefone 
			into STRICT	ds_bairro_w, 
				nr_telefone_w 
			from	compl_pf_tel_adic a 
			where	a.nr_sequencia	= nr_seq_compl_adic_w;	
		end if;	
	end if;
end if;
 
if (ie_tipo_retorno_p = 'B') then 
	ds_retorno_w := ds_bairro_w;
elsif (ie_tipo_retorno_p = 'C') then 
	ds_retorno_w := cd_cep_w;
elsif (ie_tipo_retorno_p = 'CO') then 
	ds_retorno_w := ds_complemento_w;
elsif (ie_tipo_retorno_p = 'N') then 
	ds_retorno_w := nm_prestador_w;
elsif (ie_tipo_retorno_p = 'NR') then 
	ds_retorno_w := nr_endereco_w;
elsif (ie_tipo_retorno_p = 'M') then 
	ds_retorno_w := ds_municipio_w;
elsif (ie_tipo_retorno_p = 'R') then 
	ds_retorno_w := ds_endereco_w;
elsif (ie_tipo_retorno_p = 'SG') then 
	ds_retorno_w := sg_estado_w;
elsif (ie_tipo_retorno_p = 'T') then 
	ds_retorno_w := nr_telefone_w;
elsif (ie_tipo_retorno_p = 'CGC_PF') then 
	if (cd_cgc_w IS NOT NULL AND cd_cgc_w::text <> '') then 
		ds_retorno_w := cd_cgc_w;
	elsif (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then 
		begin 
		select	nr_cpf 
		into STRICT	nr_cpf_w 
		from	pessoa_fisica 
		where	cd_pessoa_fisica = cd_pessoa_fisica_w;
		ds_retorno_w := nr_cpf_w;
		end;
	end if;
end if;
 
return 	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_dados_prest_relat ( nr_seq_prestador_p bigint, ie_tipo_retorno_p text) FROM PUBLIC;

