SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_prest_ressarc ( nr_seq_prestador_p pls_processo_prestador.nr_sequencia%type, ie_opcao_p text ) RETURNS varchar AS $body$
DECLARE

/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:   Obter dados do prestador

Pontos de atenção:
	M -  município do prestador
	UF - estado do prestador
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/ds_retorno_w 	varchar(255);


BEGIN
if (nr_seq_prestador_p IS NOT NULL AND nr_seq_prestador_p::text <> '') then
	if ( ie_opcao_p = 'M') then

		select  substr(obter_desc_municipio_ibge(a.cd_municipio_ibge),1,255)
		into STRICT 	ds_retorno_w
		from	pls_processo_prestador a
		where   a.nr_sequencia = nr_seq_prestador_p;

	elsif ( ie_opcao_p = 'UF') then

		select  substr(obter_valor_dominio(50,a.SG_ESTADO),1,255)
		into STRICT	ds_retorno_w
		from  	pls_processo_prestador a
		where   a.nr_sequencia = nr_seq_prestador_p;

	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_dados_prest_ressarc ( nr_seq_prestador_p pls_processo_prestador.nr_sequencia%type, ie_opcao_p text ) FROM PUBLIC;

