SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_prest_titulo ( nr_titulo_p bigint, ie_opcao_p text, ie_titulo_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_prestador_w		bigint := null;
ds_retorno_w			varchar(255);

-- ie_titulo_p
--'P' - Título pagar
-- ie_opcao_p
-- 'CD' - Código do prestador
BEGIN

if (upper(ie_titulo_p) = 'P') then
	select	max(b.nr_seq_prestador)
	into STRICT	nr_seq_prestador_w
	from	pls_pagamento_prestador		b,
		titulo_pagar	a
	where	b.nr_sequencia	= a.nr_seq_pls_pag_prest
	and	a.nr_titulo = nr_titulo_p;

	if (coalesce(nr_seq_prestador_w::text, '') = '') then
		select	max(b.nr_seq_prestador)
		into STRICT	nr_seq_prestador_w
		from	pls_pp_prestador b
		where	b.nr_titulo_pagar = nr_titulo_p;
	end if;

	if (coalesce(nr_seq_prestador_w::text, '') = '') then
		select	max(a.nr_seq_prestador)
		into STRICT	nr_seq_prestador_w
		from	pls_evento_movimento	a
		where	a.nr_titulo_pagar	= nr_titulo_p;
	end if;

	if (nr_seq_prestador_w IS NOT NULL AND nr_seq_prestador_w::text <> '') and (upper(ie_opcao_p) = 'CD') then
		ds_retorno_w := substr(pls_obter_cod_prestador(nr_seq_prestador_w,null),1,30);
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
