CREATE OR REPLACE FUNCTION pls_obter_dados_procedimento ( cd_procedimento_p procedimento.cd_procedimento%type, ie_origem_proc_p procedimento.ie_origem_proced%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obter dados procedimento
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

ie_opcao:

DS - Descrição
CDP - Código Porte
TXP - Taxa Porte
NTA - Número Porte Anestesia 

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w    varchar(255);


BEGIN
if (coalesce(ie_opcao_p,'X') = 'DS') then
    select  max(ds_procedimento)
    into STRICT    ds_retorno_w
    from    procedimento
    where   cd_procedimento     = cd_procedimento_p
    and ie_origem_proced    = ie_origem_proc_p;

elsif (coalesce(ie_opcao_p,'X') = 'CDP') then
       select cd_porte 
         into STRICT ds_retorno_w
         from cbhpm_preco 
       where cd_procedimento = cd_procedimento_p
         and dt_vigencia = (SELECT max(dt_vigencia) 
                              from cbhpm_preco 
                             where cd_procedimento = cd_procedimento_p
                            );
elsif (coalesce(ie_opcao_p,'X') = 'TXP') then
       select tx_porte
         into STRICT ds_retorno_w
         from cbhpm_preco 
       where cd_procedimento = cd_procedimento_p
         and dt_vigencia = (SELECT max(dt_vigencia) 
                              from cbhpm_preco 
                             where cd_procedimento = cd_procedimento_p
                            );

elsif (coalesce(ie_opcao_p,'X') = 'NTA') then
       select nr_porte_anest
         into STRICT ds_retorno_w
         from cbhpm_preco 
       where cd_procedimento = cd_procedimento_p
         and dt_vigencia = (SELECT max(dt_vigencia) 
                              from cbhpm_preco 
                             where cd_procedimento = cd_procedimento_p
                            );
end if;

return  ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
