SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_processo ( nr_seq_processo_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN
ds_retorno_w	:= null;

if (ie_opcao_p = 'NABI') then
	select	max(cd_abi)
	into STRICT	ds_retorno_w
	from	pls_processo
	where	nr_sequencia	= nr_seq_processo_p;
elsif (ie_opcao_p = 'NPROC') then
	select	max(nr_processo)
	into STRICT	ds_retorno_w
	from	pls_processo
	where	nr_sequencia	= nr_seq_processo_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
