SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_proc_guia_req (nr_seq_plano_proc_p bigint, nr_seq_req_proc_p bigint, tipo_informacao_p text) RETURNS varchar AS $body$
DECLARE


ds_procedimento_w	procedimento.ds_procedimento%type;
cd_procedimento_w	procedimento.cd_procedimento%type;
ie_origem_proced_w	procedimento.ie_origem_proced%type;
retorno_w		varchar(255);

/*
* DS -> Descrição procedimento
* CD -> Codigo procedimento
*/
BEGIN

begin
	if (nr_seq_plano_proc_p IS NOT NULL AND nr_seq_plano_proc_p::text <> '') then

		select	ie_origem_proced
		into STRICT	ie_origem_proced_w
		from	pls_guia_plano_proc
		where	nr_sequencia = nr_seq_plano_proc_p;

		select	cd_procedimento,
			substr(obter_descricao_procedimento(cd_procedimento, ie_origem_proced_w),1,255)
		into STRICT 	cd_procedimento_w,
			ds_procedimento_w
		from	pls_guia_plano_proc
		where	nr_sequencia = nr_seq_plano_proc_p;

	elsif (nr_seq_req_proc_p IS NOT NULL AND nr_seq_req_proc_p::text <> '') then

		select	ie_origem_proced
		into STRICT	ie_origem_proced_w
		from	pls_requisicao_proc
		where	nr_sequencia = nr_seq_req_proc_p;

		select	cd_procedimento,
			substr(obter_descricao_procedimento(cd_procedimento, ie_origem_proced_w),1,255)
		into STRICT 	cd_procedimento_w,
			ds_procedimento_w
		from	pls_requisicao_proc
		where	nr_sequencia = nr_seq_req_proc_p;
	end if;
exception
when others then
	cd_procedimento_w	:= null;
	ie_origem_proced_w	:= null;
end;

if (tipo_informacao_p = 'DS') then
	retorno_w	:= ds_procedimento_w;
elsif (tipo_informacao_p = 'CD') then
	retorno_w	:= cd_procedimento_w;
end if;

return	retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
