SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_prog_reaj ( nr_seq_prog_reaj_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*	ie_opcao_p
	DRL = Data reajuste lote de programação
	DE = Descrição das equipe vinculadas a programação
*/
ds_retorno_w		varchar(255);
dt_mes_reajuste_w	timestamp;

C01 CURSOR FOR
	SELECT	b.ds_equipe
	from	pls_prog_reaj_equipe a,
		pls_equipe_reajuste b
	where	b.nr_sequencia		= a.nr_seq_equipe
	and	a.nr_seq_programacao	= nr_seq_prog_reaj_p
	group by b.ds_equipe
	order by 1;

BEGIN
ds_retorno_w	:= null;

if (ie_opcao_p = 'DRL') then
	select	max(b.dt_mes_reajuste)
	into STRICT	dt_mes_reajuste_w
	from	pls_prog_reaj_coletivo		a,
		pls_prog_reaj_colet_lote	b
	where	a.nr_seq_lote	= b.nr_sequencia
	and	a.nr_sequencia	= nr_seq_prog_reaj_p;

	ds_retorno_w	:= to_char(dt_mes_reajuste_w,'dd/mm/yyyy');
elsif (ie_opcao_p = 'DE') then
	for r_c01_w in C01 loop
		begin
		if (coalesce(ds_retorno_w::text, '') = '') then
			ds_retorno_w	:= r_c01_w.ds_equipe;
		else
			ds_retorno_w	:= ds_retorno_w ||', '||r_c01_w.ds_equipe;
		end if;
		end;
	end loop;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
