SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_protocolo ( nr_seq_protocolo_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*	IE_OPCAO_P
	M - Mes competencia
	S - Situacao
	CB - Codigo beneficiario
	PF - Cdigo pessoa fisica do beneficirio
	VTE - Valor total beneficirios eventuais
	VTO - Valor total beneficirios da operadora
	PJ - Nome reduzido da pessoa jurdica da operadora
*/
ds_retorno_w			varchar(300);
ie_situacao_w			varchar(3);
dt_mes_competencia_w		timestamp;
nr_seq_segurado_w		bigint;
nr_seq_congenere_w		bigint;
nr_seq_prot_referencia_w	bigint;
ie_apresentacao_w 		varchar(30);


BEGIN
select  ie_situacao,
  dt_mes_competencia,
  nr_seq_segurado,
  nr_seq_congenere,
  substr(CASE WHEN ie_apresentacao='A' THEN obter_expressao_dic_objeto(115359) WHEN ie_apresentacao='R' THEN obter_expressao_dic_objeto(972851) END ,1,255),
  nr_seq_prot_referencia
into STRICT  ie_situacao_w,
  dt_mes_competencia_w,
  nr_seq_segurado_w,
  nr_seq_congenere_w,
  ie_apresentacao_w,
  nr_seq_prot_referencia_w
from  pls_protocolo_conta
where  nr_sequencia  = nr_seq_protocolo_p;

if (ie_opcao_p	= 'M') then
	ds_retorno_w	:= dt_mes_competencia_w;
elsif (ie_opcao_p	= 'S') then
	ds_retorno_w	:= ie_situacao_w;
elsif (ie_opcao_p	= 'CB') then
	ds_retorno_w	:= nr_seq_segurado_w;	
elsif (ie_opcao_p	= 'PF') then
	select	max(cd_pessoa_fisica)
	into STRICT	ds_retorno_w
	from	pls_segurado
	where	nr_sequencia	= nr_seq_segurado_w;
elsif (ie_opcao_p	= 'VTE') then
	select	campo_mascara_virgula(coalesce(sum(b.vl_cobrado),0))
	into STRICT	ds_retorno_w
	from	pls_segurado		c,
		pls_conta		b,
		pls_protocolo_conta	a
	where	a.nr_sequencia		= nr_seq_protocolo_p
	and	a.nr_sequencia		= b.nr_seq_protocolo
	and	b.nr_seq_segurado 	= c.nr_sequencia	
	and	c.ie_tipo_segurado 	in ('I','H');
elsif (ie_opcao_p	= 'VTO') then
	select	campo_mascara_virgula(coalesce(sum(b.vl_cobrado),0))
	into STRICT	ds_retorno_w
	from	pls_segurado		c,
		pls_conta		b,
		pls_protocolo_conta	a
	where	a.nr_sequencia		= nr_seq_protocolo_p
	and	a.nr_sequencia		= b.nr_seq_protocolo
	and	b.nr_seq_segurado 	= c.nr_sequencia
	and	c.ie_tipo_segurado 	not in ('I','H');	
elsif (ie_opcao_p	= 'PJ') then
	if (nr_seq_congenere_w IS NOT NULL AND nr_seq_congenere_w::text <> '') then
		select	a.nm_fantasia	
		into STRICT	ds_retorno_w
		from	pessoa_juridica		a,
			pls_congenere		b
		where	a.cd_cgc = b.cd_cgc
		and	b.nr_sequencia = nr_seq_congenere_w;
	end if;
  	elsif ( ie_opcao_p 	= 'TP') then
		ds_retorno_w := nr_seq_prot_referencia_w;
	elsif ( 	ie_opcao_p	= 'A') then
		ds_retorno_w := ie_apresentacao_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
