SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_ptu_fatura ( nr_seq_ptu_fatura_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
qt_dias_w		bigint;
nr_debito_credito_w	ptu_fatura.nr_nota_credito_debito%type;
dt_vencimento_fatura_w	timestamp;
dt_vencimento_ndc_w	timestamp;
dt_vencimento_a500_w	timestamp;


-- F     - Número fatura
-- VT  - Valor total fatura
-- PC  - Período câmara de compensação
-- DV  - Quantidade de dias para o vencimento
--NDC - Nota crédito / débito
-- VTN - Valor total NDC
-- DTL - Data Limite
BEGIN
if (ie_opcao_p = 'F') then
	select	max(a.nr_fatura)
	into STRICT	ds_retorno_w
	from	ptu_fatura a
	where	a.nr_sequencia = nr_seq_ptu_fatura_p;
elsif (ie_opcao_p = 'VT') then
	select	coalesce(max(a.vl_total_fatura),0)
	into STRICT	ds_retorno_w
	from	ptu_fatura a
	where	a.nr_sequencia = nr_seq_ptu_fatura_p;
elsif (ie_opcao_p = 'PC') then
	select	to_char(max(b.dt_saldo_devedor),'dd/mm/yyyy')
	into STRICT	ds_retorno_w
	from	pls_camara_calendario 		c,
		pls_camara_calend_periodo 	b,
		ptu_fatura			a
	where	b.nr_seq_calendario	= c.nr_sequencia
	and	b.nr_sequencia		= a.nr_seq_periodo
	and	a.nr_sequencia		= nr_seq_ptu_fatura_p;
elsif (ie_opcao_p = 'DV') then
	select	max(dt_vencimento_fatura),
		max(dt_vencimento_ndc)
	into STRICT	dt_vencimento_fatura_w,
		dt_vencimento_ndc_w
	from	ptu_fatura
	where	nr_sequencia	= nr_seq_ptu_fatura_p;

	if (dt_vencimento_fatura_w IS NOT NULL AND dt_vencimento_fatura_w::text <> '') and (dt_vencimento_ndc_w IS NOT NULL AND dt_vencimento_ndc_w::text <> '') then

		-- Verifica qual é a menor data - OS 587449
		if (trunc(dt_vencimento_fatura_w) < trunc(dt_vencimento_ndc_w)) then
			dt_vencimento_a500_w := dt_vencimento_fatura_w;
		else
			dt_vencimento_a500_w := dt_vencimento_ndc_w;
		end if;

	elsif (coalesce(dt_vencimento_fatura_w::text, '') = '') then
		dt_vencimento_a500_w := dt_vencimento_ndc_w;
	else
		dt_vencimento_a500_w := dt_vencimento_fatura_w;
	end if;

	qt_dias_w := trunc(dt_vencimento_a500_w) - trunc(clock_timestamp());

	if (coalesce(qt_dias_w,0) <= 0) then
		ds_retorno_w := '0';
	else
		ds_retorno_w := qt_dias_w;
	end if;

	ds_retorno_w := ds_retorno_w || ' Dia(s)';
elsif (ie_opcao_p = 'NDC')	then

	select	max(nr_nota_credito_debito)
	into STRICT	nr_debito_credito_w
	from	ptu_fatura
	where	nr_sequencia = nr_seq_ptu_fatura_p;

	ds_retorno_w	:= nr_debito_credito_w;

elsif (ie_opcao_p = 'VTN') then
	select	coalesce(max(a.vl_total_ndc),0)
	into STRICT	ds_retorno_w
	from	ptu_fatura a
	where	a.nr_sequencia = nr_seq_ptu_fatura_p;

elsif (ie_opcao_p = 'DTL') then
	select	to_char(max(b.dt_limite_a550),'dd/mm/yyyy')
	into STRICT	ds_retorno_w
	from	pls_camara_calendario 		c,
		pls_camara_calend_periodo 	b,
		ptu_fatura			a
	where	b.nr_seq_calendario	= c.nr_sequencia
	and	b.nr_sequencia		= a.nr_seq_periodo
	and	a.nr_sequencia		= nr_seq_ptu_fatura_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_dados_ptu_fatura ( nr_seq_ptu_fatura_p bigint, ie_opcao_p text) FROM PUBLIC;

