SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_ranking_prest ( nr_seq_prestador_p bigint, nr_seq_auditoria_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
nr_seq_ranking_w	bigint;
dt_auditoria_w		timestamp;


BEGIN
/* Obter os dados referente ao PRESTADOR RANKING */

if (nr_seq_prestador_p IS NOT NULL AND nr_seq_prestador_p::text <> '') then
	begin
		select	dt_auditoria
		into STRICT	dt_auditoria_w
		from	pls_auditoria
		where	nr_sequencia = nr_seq_auditoria_p;

		dt_auditoria_w := trunc(coalesce(dt_auditoria_w,clock_timestamp()));
	exception
	when others then
		dt_auditoria_w := clock_timestamp();
	end;

	select	max(nr_seq_ranking)
	into STRICT	nr_seq_ranking_w
	from	pls_prestador_ranking
	where	nr_seq_prestador	= nr_seq_prestador_p
	and	dt_auditoria_w between trunc(dt_inicio_vigencia,'dd') and fim_dia(dt_fim_vigencia);

	if (upper(ie_opcao_p) = 'CLA') then
		select	ds_cor
		into STRICT	ds_retorno_w
		from	pls_ranking_prestador
		where	nr_sequencia = nr_seq_ranking_w;

	elsif (upper(ie_opcao_p) = 'DSB') then
		select	ds_ranking_curto
		into STRICT	ds_retorno_w
		from	pls_ranking_prestador
		where	nr_sequencia = nr_seq_ranking_w;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
