SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_reajuste ( nr_seq_reajuste_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*	ie_opcao_p
	'P' - Percentual do reajuste
	'PC' - Percentual do reajuste da coparticipação
	'TRP' - Tipo de reajuste programação
	'S' - Status do reajuste
	'DS' - Descrição do status
*/
tx_reajuste_w			double precision;
tx_reajuste_copartic_w		double precision;
ds_retorno_w			varchar(255);
dt_reajuste_w			timestamp;
ie_status_w			pls_reajuste.ie_status%type;


BEGIN

if (coalesce(nr_seq_reajuste_p,0) > 0) then
	select	coalesce(tx_reajuste,0),
		coalesce(tx_reajuste_copartic,0),
		dt_reajuste,
		ie_status
	into STRICT	tx_reajuste_w,
		tx_reajuste_copartic_w,
		dt_reajuste_w,
		ie_status_w
	from	pls_reajuste
	where	nr_sequencia	= nr_seq_reajuste_p;

	if (ie_opcao_p = 'P') then
		ds_retorno_w	:= to_char(tx_reajuste_w);
	elsif (ie_opcao_p = 'PC') then
		ds_retorno_w	:= to_char(tx_reajuste_copartic_w);
	elsif (ie_opcao_p = 'D') then
		ds_retorno_w	:= to_char(dt_reajuste_w,'dd/mm/yyyy');
	elsif (ie_opcao_p = 'TRP') then
		select	max(nr_seq_tipo_reajuste)
		into STRICT	ds_retorno_w
		from	pls_reajuste
		where	nr_sequencia	= nr_seq_reajuste_p;

		if (coalesce(ds_retorno_w::text, '') = '') then
			select	b.ds_tipo_reajuste
			into STRICT	ds_retorno_w
			from	pls_prog_reaj_coletivo	a,
				pls_tipo_reajuste	b
			where	a.nr_seq_tipo_reajuste	= b.nr_sequencia
			and	a.nr_seq_reajuste	= nr_seq_reajuste_p;
		end if;
	elsif (ie_opcao_p = 'S') then
		ds_retorno_w	:= ie_status_w;
	elsif (ie_opcao_p = 'DS') then
		ds_retorno_w	:= substr(obter_valor_dominio(1916,ie_status_w),1,255);
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
