SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_recalculo ( nr_seq_conta_proc_p pls_conta_proc.nr_sequencia%type, nr_seq_conta_mat_p pls_conta_mat.nr_sequencia%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
ie_opcao_p
VO - Valor Original
L  - Número do lote de recálculo
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w			varchar(15);
nr_seq_item_recalculo_w		pls_item_recalculo.nr_sequencia%type;



BEGIN

if (nr_seq_conta_proc_p IS NOT NULL AND nr_seq_conta_proc_p::text <> '') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_item_recalculo_w
	from	pls_item_recalculo
	where	nr_seq_procedimento = nr_seq_conta_proc_p;
else
	select	max(nr_sequencia)
	into STRICT	nr_seq_item_recalculo_w
	from	pls_item_recalculo
	where	nr_seq_material = nr_seq_conta_mat_p;
end if;

if (nr_seq_item_recalculo_w IS NOT NULL AND nr_seq_item_recalculo_w::text <> '') then

	if (ie_opcao_p = 'L') then

		select	max(nr_seq_lote)
		into STRICT	ds_retorno_w
		from	pls_conta_recalculo a,
			pls_item_recalculo b
		where	a.nr_sequencia = b.nr_seq_conta
		and	b.nr_sequencia = nr_seq_item_recalculo_w;

	elsif (ie_opcao_p = 'VO') then

		select	max(vl_item_atual)
		into STRICT	ds_retorno_w
		from	pls_item_recalculo
		where	nr_sequencia = nr_seq_item_recalculo_w;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
