CREATE OR REPLACE FUNCTION pls_obter_dados_rec_glosa_aud ( nr_seq_rec_glosa_aud_p pls_rec_glosa_conta_aud.nr_sequencia%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255) := null;
nr_sequencia_w		bigint := null;


BEGIN
if (ie_opcao_p = 'NAM') then
	select	max(nr_seq_grupo_membro_med)
	into STRICT	nr_sequencia_w
	from	pls_rec_glosa_conta_aud
	where	nr_sequencia	= nr_seq_rec_glosa_aud_p;

	if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
		select	substr(obter_nome_usuario(nm_usuario_exec),1,80)
		into STRICT	ds_retorno_w
		from	pls_membro_grupo_aud
		where	ie_auditor_ptu	= 'S'
		and	nr_sequencia	= nr_sequencia_w;
	end if;

elsif (ie_opcao_p = 'NAE') then
	select	max(nr_seq_grupo_membro_enf)
	into STRICT	nr_sequencia_w
	from	pls_rec_glosa_conta_aud
	where	nr_sequencia	= nr_seq_rec_glosa_aud_p;

	if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
		select	substr(obter_nome_usuario(nm_usuario_exec),1,80)
		into STRICT	ds_retorno_w
		from	pls_membro_grupo_aud
		where	ie_auditor_ptu	= 'S'
		and	nr_sequencia	= nr_sequencia_w;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
