CREATE OR REPLACE FUNCTION pls_obter_dados_rec_glosa_mat (nr_seq_rec_glosa_mat_p pls_rec_glosa_mat.nr_sequencia%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
nr_seq_mat_imp_w	pls_rec_glosa_mat.nr_sequencia%type;

/*
ie_opcao_p
	'CDM'	-	Código material
	'DS'	-	Descrição material
	'CDT'	-	Código do tipo da tabela
	'VLR'	-	Valor recursado
	'NRC'	-	Número da conta
	'NRM'	-	Número do material
*/
BEGIN

select	max(nr_seq_conta_mat_imp)
into STRICT	nr_seq_mat_imp_w
from	pls_rec_glosa_mat
where	nr_sequencia = nr_seq_rec_glosa_mat_p;

if (ie_opcao_p = 'CDM') then
	select	max(cd_material)
	into STRICT	ds_retorno_w
	from	pls_rec_glosa_mat_imp
	where	nr_sequencia = nr_seq_mat_imp_w;

elsif (ie_opcao_p = 'DS') then
	select	max(ds_material)
	into STRICT	ds_retorno_w
	from	pls_rec_glosa_mat_imp
	where	nr_sequencia = nr_seq_mat_imp_w;

elsif (ie_opcao_p = 'CDT') then
	select	max(cd_tipo_tabela)
	into STRICT	ds_retorno_w
	from	pls_rec_glosa_mat_imp
	where	nr_sequencia = nr_seq_mat_imp_w;

elsif (ie_opcao_p = 'VLR') then
	select	max(vl_recursado)
	into STRICT	ds_retorno_w
	from	pls_rec_glosa_mat_imp
	where	nr_sequencia = nr_seq_mat_imp_w;

elsif (ie_opcao_p = 'NRC') then
	select	max(nr_seq_conta)
	into STRICT	ds_retorno_w
	from	pls_rec_glosa_mat_imp
	where	nr_sequencia = nr_seq_mat_imp_w;

elsif (ie_opcao_p = 'NRM') then
	select	max(nr_seq_material)
	into STRICT	ds_retorno_w
	from	pls_rec_glosa_mat_imp
	where	nr_sequencia = nr_seq_mat_imp_w;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
