SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_rec_glosa_mens (ie_tipo_item_p text, ie_opcao_p text, nr_seq_item_mens_p bigint) RETURNS bigint AS $body$
DECLARE

 
/* 
ie_opcao_p 
1 - Valor 
2 - Conta Recurso Glosa 
*/
 
 
vl_retorno_w		double precision;
				

BEGIN 
 
if (ie_tipo_item_p = '3') then 
	if (ie_opcao_p = '1') then 
		select coalesce(sum(b.vl_coparticipacao),0) 
		into STRICT	vl_retorno_w 
		from  pls_mensalidade_item_conta a, 
			pls_conta_coparticipacao b 
		where  b.nr_sequencia = a.nr_seq_conta_copartic 
		and   a.nr_seq_item = nr_seq_item_mens_p 
		and   (b.nr_seq_conta_rec IS NOT NULL AND b.nr_seq_conta_rec::text <> '');
	elsif (ie_opcao_p = '2') then 
		select max(b.nr_seq_conta_rec) 
		into STRICT	vl_retorno_w 
		from  pls_mensalidade_item_conta a, 
			pls_conta_coparticipacao b 
		where  b.nr_sequencia = a.nr_seq_conta_copartic 
		and   a.nr_seq_item = nr_seq_item_mens_p;
	end if;
elsif (ie_tipo_item_p in ('6','7')) then 
	if (ie_opcao_p = '1') then 
		select coalesce(sum(b.vl_beneficiario),0) 
		into STRICT	vl_retorno_w 
		from  pls_mensalidade_item_conta a, 
			pls_conta_pos_estabelecido b 
		where  b.nr_sequencia = a.nr_seq_conta_pos_estab 
		and   a.nr_seq_item = nr_seq_item_mens_p 
		and   (b.nr_seq_conta_rec IS NOT NULL AND b.nr_seq_conta_rec::text <> '');
		 
		if (coalesce(vl_retorno_w,0) = 0) then 
			select	coalesce(sum(a.vl_item),0) 
			into STRICT	vl_retorno_w 
			from	pls_mensalidade_item_conta a, 
				pls_conta_pos_proc b 
			where	b.nr_sequencia = a.nr_seq_pos_proc			 
			and	a.nr_seq_item = nr_seq_item_mens_p 
			and	(b.nr_seq_conta_rec IS NOT NULL AND b.nr_seq_conta_rec::text <> '');
		end if;
	elsif (ie_opcao_p = '2') then 
		select max(b.nr_seq_conta_rec) 
		into STRICT	vl_retorno_w 
		from  pls_mensalidade_item_conta a, 
			pls_conta_pos_estabelecido b 
		where  b.nr_sequencia = a.nr_seq_conta_pos_estab 
		and   a.nr_seq_item = nr_seq_item_mens_p;
		 
		if (coalesce(vl_retorno_w,0) = 0) then 
			select	max(b.nr_seq_conta_rec) 
			into STRICT	vl_retorno_w 
			from	pls_mensalidade_item_conta a, 
				pls_conta_pos_proc b 
			where	b.nr_sequencia = a.nr_seq_pos_proc			 
			and	a.nr_seq_item = nr_seq_item_mens_p;
		end if;
	end if;
end if;
 
return	vl_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
