CREATE OR REPLACE FUNCTION pls_obter_dados_rec_glosa_proc (nr_seq_rec_glosa_proc_p pls_rec_glosa_proc.nr_sequencia%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
nr_seq_proc_imp_w	pls_rec_glosa_proc.nr_sequencia%type;

/*
ie_opcao_p
	'CDP'	-	Código procedimento
	'DS'	-	Descrição procedimento
	'DTR'	-	Data realização
	'DTF'	-	Data fim internação
	'CDT'	-	Código tipo tabela
	'VLR'	-	Valor recursado
*/
BEGIN

select	max(nr_seq_conta_proc_imp)
into STRICT	nr_seq_proc_imp_w
from	pls_rec_glosa_proc
where	nr_sequencia = nr_seq_rec_glosa_proc_p;

if (ie_opcao_p = 'CDP') then
	select	max(cd_procedimento)
	into STRICT	ds_retorno_w
	from	pls_rec_glosa_proc_imp
	where	nr_sequencia = nr_seq_proc_imp_w;

elsif (ie_opcao_p = 'DS') then
	select	max(ds_procedimento)
	into STRICT	ds_retorno_w
	from	pls_rec_glosa_proc_imp
	where	nr_sequencia = nr_seq_proc_imp_w;

elsif (ie_opcao_p = 'DTR') then
	select	max(dt_realizacao)
	into STRICT	ds_retorno_w
	from	pls_rec_glosa_proc_imp
	where	nr_sequencia = nr_seq_proc_imp_w;

elsif (ie_opcao_p = 'CDT') then
	select	max(cd_tipo_tabela)
	into STRICT	ds_retorno_w
	from	pls_rec_glosa_proc_imp
	where	nr_sequencia = nr_seq_proc_imp_w;

elsif (ie_opcao_p = 'VLR') then
	select	max(vl_recursado)
	into STRICT	ds_retorno_w
	from	pls_rec_glosa_proc_imp
	where	nr_sequencia = nr_seq_proc_imp_w;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
