CREATE OR REPLACE FUNCTION pls_obter_dados_rede_atend ( nr_seq_rede_atend_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*	ie_opcao_p:
	DS = Descrição
	OB = Observação
	CR = Código rede
*/
ds_retorno_w		varchar(4000);
ds_observacao_w		varchar(4000);


BEGIN


if (ie_opcao_p = 'DS') then
	select	max(ds_rede)
	into STRICT	ds_retorno_w
	from	pls_rede_atendimento
	where	nr_sequencia	= nr_seq_rede_atend_p;
elsif (ie_opcao_p = 'OB') then
	select	max(ds_observacao)
	into STRICT	ds_retorno_w
	from	pls_rede_atendimento
	where	nr_sequencia	= nr_seq_rede_atend_p;
elsif (ie_opcao_p = 'CR') then
	select	max(cd_rede)
	into STRICT	ds_retorno_w
	from	pls_rede_atendimento
	where	nr_sequencia	= nr_seq_rede_atend_p;
else
	ds_retorno_w	:= 'Opção incorreta!';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
