CREATE OR REPLACE FUNCTION pls_obter_dados_regra_compl ( nr_seq_regra_compl_p pls_regra_conta_compl.nr_sequencia%type, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
Obter os dados da Regra complemento

ie_tipo_p:
DF - Solicitar Documento Físico
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [ x ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w		varchar(255)	:= 'S';


BEGIN
if (nr_seq_regra_compl_p IS NOT NULL AND nr_seq_regra_compl_p::text <> '') then
	if (ie_tipo_p = 'DF') then
		begin
			select	coalesce(ie_solic_doc_fisico,'S')
			into STRICT	ds_retorno_w
			from	pls_regra_conta_compl
			where	nr_sequencia = nr_seq_regra_compl_p;
		exception
		when others then
			ds_retorno_w	:= 'S';
		end;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
