SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_regra_copart (nr_seq_regra_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
	D	- Descrição da estrutura coparticipação
	TX	- Taxa coparticipação
	VL	- Valor coparticipação
	VM	- Valor máximo
*/
ds_coparticipacao_w		varchar(255);
ds_retorno_w			varchar(255);
tx_coparticipacao_w		double precision;
vl_maximo_w			double precision;
vl_coparticipacao_w		double precision;


BEGIN

select	b.ds_tipo_coparticipacao,
	a.tx_coparticipacao,
	a.vl_maximo,
	a.vl_coparticipacao
into STRICT	ds_coparticipacao_w,
	tx_coparticipacao_w,
	vl_maximo_w,
	vl_coparticipacao_w
from	pls_regra_coparticipacao	a,
	pls_tipo_coparticipacao		b
where	a.nr_seq_tipo_coparticipacao	= b.nr_sequencia
and	a.nr_sequencia			= nr_seq_regra_p;

if (ie_opcao_p = 'D') then
	ds_retorno_w	:= ds_coparticipacao_w;
elsif (ie_opcao_p = 'TX') then
	ds_retorno_w	:= coalesce(tx_coparticipacao_w,0);
elsif (ie_opcao_p = 'VL') then
	ds_retorno_w	:= coalesce(vl_coparticipacao_w,0);
elsif (ie_opcao_p = 'VM') then
	ds_retorno_w	:= coalesce(vl_maximo_w,0);
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
