SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_regra_escala ( nr_seq_regra_escala_p bigint, nm_usuario_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

				 
/* 
ie_opcao_p 
D - Descrição da regra, nome do vendedor com o vendedor PF se tiver 
L - Apresenta apenas os vendedores logados da regra conforme o usuário 
*/
 
			 
ds_retorno_w			varchar(255);
nr_seq_vendedor_w		bigint;
nr_seq_vendedor_vinculado_w	bigint;
qt_registros_w			bigint;


BEGIN 
 
if (ie_opcao_p = 'D') then 
	select	nr_seq_vendedor, 
		NR_SEQ_VENDEDOR_VINCULADO 
	into STRICT	nr_seq_vendedor_w, 
		nr_seq_vendedor_vinculado_w 
	from	PLS_SOLICITACAO_REGRA_ATEN 
	where	nr_sequencia		= nr_seq_regra_escala_p;
	 
	select	substr(pls_obter_dados_vendedor(nr_seq_vendedor_w,'N') || CASE WHEN coalesce(nr_seq_vendedor_vinculado_w::text, '') = '' THEN  ''  ELSE ' (' || pls_obter_dados_vendedor_vinc(nr_seq_vendedor_vinculado_w,'N') || ')' END ,1,255) 
	into STRICT	ds_retorno_w 
	;
 
elsif (ie_opcao_p = 'L') then 
	select	nr_seq_vendedor, 
		NR_SEQ_VENDEDOR_VINCULADO 
	into STRICT	nr_seq_vendedor_w, 
		nr_seq_vendedor_vinculado_w 
	from	PLS_SOLICITACAO_REGRA_ATEN 
	where	nr_sequencia		= nr_seq_regra_escala_p;
	 
	select	count(1) 
	into STRICT	qt_registros_w 
	from	pls_vendedor		b, 
		usuario			a 
	where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica 
	and	b.nr_sequencia		= nr_seq_vendedor_w 
	and	a.nm_usuario		= nm_usuario_p;
	 
	if (qt_registros_w > 0) then 
		ds_retorno_w	:= 'S';
	elsif (qt_registros_w = 0) and (nr_seq_vendedor_vinculado_w IS NOT NULL AND nr_seq_vendedor_vinculado_w::text <> '') then 
		select	count(1) 
		into STRICT	qt_registros_w 
		from	pls_vendedor_vinculado	b, 
			usuario			a 
		where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica 
		and	b.nr_sequencia		= nr_seq_vendedor_vinculado_w 
		and	a.nm_usuario		= nm_usuario_p;
		 
		if (qt_registros_w > 0) then 
			ds_retorno_w	:= 'S';
		else 
			ds_retorno_w	:= 'N';
		end if;
	else 
		ds_retorno_w	:= 'N';
	end if;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
