SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_regra_inscr (nr_seq_regra_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
	TX	- Taxa inscrição
	VL	- Valor inscrição
*/
ds_retorno_w		varchar(255);
tx_inscricao_w		double precision;
vl_inscricao_w		double precision;


BEGIN

select	vl_inscricao,
	tx_inscricao
into STRICT	vl_inscricao_w,
	tx_inscricao_w
from	pls_regra_inscricao
where	nr_sequencia	= nr_seq_regra_p;

if (ie_opcao_p = 'TX') then
	ds_retorno_w	:= coalesce(tx_inscricao_w,0);
elsif (ie_opcao_p = 'VL') then
	ds_retorno_w	:= coalesce(vl_inscricao_w,0);
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
