CREATE OR REPLACE FUNCTION pls_obter_dados_rel_itamed_870 ( qt_idade_p bigint, nr_seq_titular_p bigint, nr_seq_pagador_p bigint, nr_cpf_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w			varchar(255)	:= 'S';
qt_idade_titular_w		bigint;

BEGIN
if (coalesce(nr_cpf_p::text, '') = '') then
	if (qt_idade_p > 18) then
		ds_retorno_w := 'N';
	else
		if (coalesce(nr_seq_titular_p::text, '') = '') then
			select	(CASE WHEN (max(a.nr_cpf) IS NOT NULL AND (max(a.nr_cpf))::text <> '') THEN 'S' ELSE 'N' END)
			into STRICT	ds_retorno_w
			from	pessoa_fisica		a,
				pls_contrato_pagador	b
			where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica
			and	b.nr_sequencia		= nr_seq_pagador_p;

			if (ds_retorno_w = 'N') then
				select	(CASE WHEN (max(cd_cgc) IS NOT NULL AND (max(cd_cgc))::text <> '') THEN 'S' ELSE 'N' END)
				into STRICT	ds_retorno_w
				from	pls_contrato_pagador
				where	nr_sequencia	= nr_seq_pagador_p;
			end if;
		else
			begin
			select	(CASE WHEN (max(a.nr_cpf) IS NOT NULL AND (max(a.nr_cpf))::text <> '') THEN 'S' ELSE 'N' END)
			into STRICT	ds_retorno_w
			from	pessoa_fisica	a,
				pls_segurado	b
			where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica
			and	b.nr_sequencia		= nr_seq_titular_p;
			if (ds_retorno_w = 'N') then
				begin
				select	obter_idade(a.dt_nascimento,clock_timestamp(),'A')
				into STRICT	qt_idade_titular_w
				from	pessoa_fisica	a,
					pls_segurado	b
				where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica
				and	b.nr_sequencia		= nr_seq_titular_p;
				if (qt_idade_titular_w < 18) then
					select	(CASE WHEN (max(a.nr_cpf) IS NOT NULL AND (max(a.nr_cpf))::text <> '') THEN 'S' ELSE 'N' END)
					into STRICT	ds_retorno_w
					from	pessoa_fisica		a,
						pls_segurado		b,
						pls_contrato_pagador	c
					where	a.cd_pessoa_fisica	= c.cd_pessoa_fisica
					and	c.nr_sequencia		= b.nr_seq_pagador
					and	b.nr_sequencia		= nr_seq_titular_p;
					if (ds_retorno_w = 'N') then
						select	(CASE WHEN (max(b.cd_cgc) IS NOT NULL AND (max(b.cd_cgc))::text <> '') THEN 'S' ELSE 'N' END)
						into STRICT	ds_retorno_w
						from	pls_segurado		a,
							pls_contrato_pagador	b
						where	b.nr_sequencia		= a.nr_seq_pagador
						and	a.nr_sequencia		= nr_seq_titular_p;
					end if;
				end if;
				end;
			end if;
			end;
		end if;
	end if;
end if;
return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
