SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_responsavel_pj ( cd_cgc_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

			 
/* 
N - Nome 
C - Cargo 
*/
 
			 
ds_retorno_w		varchar(255);
nr_seq_resp_jur_w	bigint;


BEGIN 
 
select	max(nr_sequencia) 
into STRICT	nr_seq_resp_jur_w 
from	PESSOA_JUR_RESP 
where	cd_cgc	= cd_cgc_p 
and	IE_RESP_CADASTRO	= 'S';	
 
if (nr_seq_resp_jur_w IS NOT NULL AND nr_seq_resp_jur_w::text <> '') then 
	if (ie_opcao_p = 'N') then 
		select	substr(obter_nome_pf(CD_PESSOA_FISICA),1,255) 
		into STRICT	ds_retorno_w 
		from	PESSOA_JUR_RESP 
		where	nr_sequencia	= nr_seq_resp_jur_w;
	elsif (ie_opcao_p = 'C') then 
		select	substr(obter_desc_cargo(CD_CARGO),1,255) 
		into STRICT	ds_retorno_w 
		from	PESSOA_JUR_RESP 
		where	nr_sequencia	= nr_seq_resp_jur_w;
	end if;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
