SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_res_prot ( nr_seq_prot_conta_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/* ie_opcao_p
T	= Título
NF	= Nr. sequência da nota fiscal
*/
ds_retorno_w		varchar(255);
nr_seq_nf_w			bigint;
nr_titulo_w			bigint;


BEGIN

select	max(nr_titulo)
into STRICT		nr_titulo_w
from		titulo_pagar
where		nr_seq_prot_conta = nr_seq_prot_conta_p;

select	max(nr_sequencia)
into STRICT		nr_seq_nf_w
from		nota_fiscal
where		nr_seq_prot_res_pls = nr_seq_prot_conta_p;

if (ie_opcao_p = 'T') then
	ds_retorno_w := to_char(nr_titulo_w);
elsif (ie_opcao_p	= 'NF') then
	ds_retorno_w := to_char(nr_seq_nf_w);
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
