CREATE OR REPLACE FUNCTION pls_obter_dados_revisao_pessoa ( nr_seq_lote_p bigint, ie_tipo_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*	ie_tipo_opcao_p
	TC	Tipos de consistência
*/
ds_retorno_w		varchar(255)	:= null;
ie_pagador_w		pls_lote_pessoa_inconsist.ie_pagador%type;
ie_estipulante_w	pls_lote_pessoa_inconsist.ie_estipulante%type;
ie_beneficiario_w	pls_lote_pessoa_inconsist.ie_beneficiario%type;
ie_prestador_w		pls_lote_pessoa_inconsist.ie_prestador%type;


BEGIN

select	coalesce(ie_pagador,'N'),
	coalesce(ie_estipulante,'N'),
	coalesce(ie_beneficiario,'N'),
	coalesce(ie_prestador,'N')
into STRICT	ie_pagador_w,
	ie_estipulante_w,
	ie_beneficiario_w,
	ie_prestador_w
from	pls_lote_pessoa_inconsist
where	nr_sequencia	= nr_seq_lote_p;

if (ie_tipo_opcao_p = 'TC') then
	if (ie_pagador_w = 'S') then
		ds_retorno_w	:= 'Pagador';
	end if;

	if (ie_estipulante_w = 'S') then
		if (coalesce(ds_retorno_w::text, '') = '') then
			ds_retorno_w	:= 'Estipulante';
		else
			ds_retorno_w	:= ds_retorno_w || ', Estipulante';
		end if;
	end if;

	if (ie_beneficiario_w = 'S') then
		if (coalesce(ds_retorno_w::text, '') = '') then
			ds_retorno_w	:= 'Beneficiário';
		else
			ds_retorno_w	:= ds_retorno_w || ', Beneficiário';
		end if;
	end if;

	if (ie_prestador_w = 'S') then
		if (coalesce(ds_retorno_w::text, '') = '') then
			ds_retorno_w	:= 'Prestador';
		else
			ds_retorno_w	:= ds_retorno_w || ', Prestador';
		end if;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
