CREATE OR REPLACE FUNCTION pls_obter_dados_sca_obito ( nr_seq_regra_obito_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_opcao_p
BN - Nome da bonificação
SN - Nome do SCA da regra de bonificação
*/
nr_seq_sca_w		bigint;
nr_seq_bonificacao_w	bigint;


BEGIN

select	nr_seq_sca,
	nr_seq_bonificacao
into STRICT	nr_seq_sca_w,
	nr_seq_bonificacao_w
from	PLS_SCA_REGRA_OBITO
where	nr_sequencia		= nr_seq_regra_obito_p;

if (ie_opcao_p = 'BN') then
	if (nr_seq_bonificacao_w IS NOT NULL AND nr_seq_bonificacao_w::text <> '') then
		return pls_obter_desc_bonificacao(nr_seq_bonificacao_w);
	end if;
elsif (ie_opcao_p = 'SN') then
	if (nr_seq_sca_w IS NOT NULL AND nr_seq_sca_w::text <> '') then
		return pls_obter_dados_produto(nr_seq_sca_w,'N');
	end if;
end if;

return '';

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
