CREATE OR REPLACE FUNCTION pls_obter_dados_sca_perfil ( nr_seq_proposta_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);
nr_seq_simul_perfil_w		bigint;
nr_seq_plano_sca_w		bigint;
nr_seq_tabela_sca_w		bigint;

C01 CURSOR FOR
	SELECT	nr_seq_plano,
		nr_seq_tabela
	from	pls_sca_vinculo
	where	nr_seq_simulacao_perfil	= nr_seq_simul_perfil_w;


BEGIN

select	NR_SEQ_SIMUL_PERFIL
into STRICT	nr_seq_simul_perfil_w
from	pls_proposta_adesao
where	nr_sequencia	= nr_seq_proposta_p;

if (coalesce(nr_seq_simul_perfil_w::text, '') = '') then
	return '';
end if;

open C01;
loop
fetch C01 into
	nr_seq_plano_sca_w,
	nr_seq_tabela_sca_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	ds_retorno_w	:= ds_retorno_w||'#'||nr_seq_plano_sca_w||';'||nr_seq_tabela_sca_w||';';

	end;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
