SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_segurado_imp ( cd_usuario_plano_p text, ie_campo_retorno_p text, ie_origem_p text) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obter os dados do segurado importado nas transacoes TISS via Webservice
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[  ]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  x] Portal [  ]  Relatorios [x ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atencao: 
IE_ORIGEM_P
G - Guia

IE_CAMPO_RETORNO_P
SS - Sequencia segurado
SP - Sequencia do produto do segurado
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
cd_medico_w		pessoa_fisica.cd_pessoa_fisica%type := null;
nr_seq_segurado_w	pls_segurado.nr_sequencia%type;
nr_seq_plano_w		pls_plano.nr_sequencia%type;
ie_valida_zero_w	varchar(1) := 'N';

cd_usuario_plano_w	varchar(30);
ds_retorno_w		varchar(10);


BEGIN

--Origem Guia
if ( ie_origem_p = 'G' ) then
	
	ie_valida_zero_w := pls_obter_param_padrao_funcao(3,1249);
	cd_usuario_plano_w := cd_usuario_plano_p;
	
	if ( ie_valida_zero_w = 'S') then
		if (length( cd_usuario_plano_w ) = 16) then
			cd_usuario_plano_w := '0' || cd_usuario_plano_w;
		end if;
	end if;
	
	select	max(a.nr_sequencia)
	into STRICT	nr_seq_segurado_w
	from	pls_segurado			a,
			pls_segurado_carteira	b
	where	b.nr_seq_segurado		= a.nr_sequencia
	and		b.nr_cartao_intercambio	= lpad(cd_usuario_plano_w,17,'0')
	and		coalesce(a.dt_rescisao::text, '') = ''
	and		coalesce(a.dt_cancelamento::text, '') = '';
	
	if (coalesce(nr_seq_segurado_w::text, '') = '') then
		select	max(a.nr_sequencia)
		into STRICT	nr_seq_segurado_w
		from	pls_segurado			a,
				pls_segurado_carteira	b
		where	b.nr_seq_segurado	= a.nr_sequencia
		and		b.cd_usuario_plano 	= cd_usuario_plano_w;
	end if;		
end if;

-- Retorna codigo da pessoa fisica
if ( ie_campo_retorno_p = 'SS' ) then
	ds_retorno_w := nr_seq_segurado_w;
elsif ( ie_campo_retorno_p = 'SP' ) then
	ds_retorno_w := pls_obter_produto_benef(nr_seq_segurado_w, clock_timestamp());
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_dados_segurado_imp ( cd_usuario_plano_p text, ie_campo_retorno_p text, ie_origem_p text) FROM PUBLIC;

