CREATE OR REPLACE FUNCTION pls_obter_dados_seg_mes ( nr_seq_segurado_p bigint, dt_mesano_ref_p timestamp, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/* ie_opcao_p
	S = Status
*/
ds_retorno_w		varchar(255);
dt_rescisao_w		timestamp;
dt_reativacao_w		timestamp;
dt_adesao_w		timestamp;
ie_tipo_registro_w	smallint;
nr_seq_segurado_w	bigint;


BEGIN

select	nr_sequencia,
	dt_rescisao,
	dt_reativacao,
	dt_contratacao
into STRICT	nr_seq_segurado_w,
	dt_rescisao_w,
	dt_reativacao_w,
	dt_adesao_w
from	pls_segurado
where	nr_sequencia	= nr_seq_segurado_p;

if 	((coalesce(dt_rescisao_w::text, '') = '') or (trunc(dt_mesano_ref_p,'month') < trunc(coalesce(dt_rescisao_w,dt_mesano_ref_p),'month')) or (trunc(dt_mesano_ref_p,'month') >= trunc(coalesce(dt_reativacao_w,add_months(dt_mesano_ref_p, -1)),'month') and (dt_rescisao_w < dt_reativacao_w))) and (dt_mesano_ref_p >= dt_adesao_w) then
	ie_tipo_registro_w := 1; -- Ativo
else
	ie_tipo_registro_w := 3; -- Inativo
end if;

if (ie_opcao_p = 'S') then
	ds_retorno_w := to_char(ie_tipo_registro_w);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
