SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_sib (nr_seq_lote_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*	ie_opcao_p
	'DR' - Data de referencia
	'DE' - Data de envio
*/
ds_retorno_w			varchar(255);
dt_mesano_referencia_w	timestamp;
dt_envio_w				timestamp;


BEGIN

select	dt_mesano_referencia,
		dt_envio
into STRICT	dt_mesano_referencia_w,
		dt_envio_w
from	pls_lote_sib
where	nr_sequencia	= nr_seq_lote_p;

if (ie_opcao_p = 'DR') then
	ds_retorno_w	:= to_char(dt_mesano_referencia_w,'dd/mm/yyyy');
elsif (ie_opcao_p = 'DE') then
	ds_retorno_w	:= to_char(dt_envio_w,'dd/mm/yyyy');
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
