CREATE OR REPLACE FUNCTION pls_obter_dados_solicitacao ( nr_seq_solicitacao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
/*ie_opcao_p 
N - Nome da pessoa 
DN - Data de nascimento 
NT - Número do telefone 
E - E-mail 
S - Status 
O - Origem da solicitação 
DS - Data da solicitação 
V - Vendedor atual 
DH - Data do último histórico 
*/
 
 
ds_retorno_w		varchar(255);
nm_pessoa_fisica_w	varchar(60);
dt_nascimento_w		timestamp;
nr_telefone_w		varchar(40);
ds_email_w		varchar(255);
ds_status_w		varchar(255);
ds_origem_w		varchar(200);
dt_solicitacao_w	timestamp;
nr_seq_canal_w		bigint;
dt_historico_w		timestamp;


BEGIN 
 
if (ie_opcao_p = 'N') then 
	select	nm_pessoa_fisica 
	into STRICT	ds_retorno_w 
	from	pls_solicitacao_comercial 
	where	nr_sequencia	= nr_seq_solicitacao_p;
elsif (ie_opcao_p = 'DN') then 
	select	to_char(dt_nascimento) 
	into STRICT	ds_retorno_w 
	from	pls_solicitacao_comercial 
	where	nr_sequencia	= nr_seq_solicitacao_p;
elsif (ie_opcao_p = 'NT') then 
	select	nr_telefone 
	into STRICT	ds_retorno_w 
	from	pls_solicitacao_comercial 
	where	nr_sequencia	= nr_seq_solicitacao_p;
elsif (ie_opcao_p = 'E') then 
	select	ds_email 
	into STRICT	ds_retorno_w 
	from	pls_solicitacao_comercial 
	where	nr_sequencia	= nr_seq_solicitacao_p;
elsif (ie_opcao_p = 'S') then 
	select	substr(obter_valor_dominio(2461, ie_status),1,100) 
	into STRICT	ds_retorno_w 
	from	pls_solicitacao_comercial 
	where	nr_sequencia	= nr_seq_solicitacao_p;
elsif (ie_opcao_p = 'O') then 
	select	substr(obter_valor_dominio(2471, ie_origem_solicitacao),1,100) 
	into STRICT	ds_retorno_w 
	from	pls_solicitacao_comercial 
	where	nr_sequencia	= nr_seq_solicitacao_p;
elsif (ie_opcao_p = 'DS') then 
	select	to_char(dt_solicitacao,'dd/mm/yyyy') 
	into STRICT	ds_retorno_w 
	from	pls_solicitacao_comercial 
	where	nr_sequencia	= nr_seq_solicitacao_p;
elsif (ie_opcao_p = 'V') then 
	select	max(nr_sequencia) 
	into STRICT	nr_seq_canal_w 
	from	pls_solicitacao_vendedor 
	where	nr_seq_solicitacao	= nr_seq_solicitacao_p 
	and	coalesce(dt_fim_vigencia::text, '') = '';
	 
	if (coalesce(nr_seq_canal_w,0) <> 0) then 
		select	substr(obter_nome_pf_pj(cd_pessoa_fisica,cd_cgc),1,255) 
		into STRICT	ds_retorno_w 
		from	pls_solicitacao_vendedor a, 
			pls_vendedor b 
		where	b.nr_sequencia	= a.nr_seq_vendedor_canal 
		and	a.nr_sequencia	= nr_seq_canal_w;
	end if;
elsif (ie_opcao_p = 'DH') then 
	select	max(dt_historico) 
	into STRICT	dt_historico_w 
	from	pls_solicitacao_historico 
	where	nr_seq_solicitacao	= nr_seq_solicitacao_p;
	 
	if (dt_historico_w IS NOT NULL AND dt_historico_w::text <> '') then 
		ds_retorno_w	:= to_char(dt_historico_w,'dd/mm/yyyy');
	end if;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_dados_solicitacao ( nr_seq_solicitacao_p bigint, ie_opcao_p text) FROM PUBLIC;

