CREATE OR REPLACE FUNCTION pls_obter_dados_solic_resc ( nr_seq_solicitacao_p pls_solicitacao_rescisao.nr_sequencia%type, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


/*	ie_tipo_p
	LMR - Lista com Motivos de rescis√£o
*/
ds_retorno_w	varchar(4000);

C01 CURSOR FOR
	SELECT  substr(pls_obter_desc_motivo_cancel(nr_seq_motivo_rescisao),1,255)ds_motivo_cancelamento
	from	pls_solic_rescisao_benef
	where	nr_seq_solicitacao = nr_seq_solicitacao_p
	group by nr_seq_motivo_rescisao;

BEGIN

if (ie_tipo_p = 'LMR') then
	for r_c01_w in c01 loop
		begin
		if (coalesce(ds_retorno_w::text, '') = '') then
			ds_retorno_w := r_c01_w.ds_motivo_cancelamento;
		else
			ds_retorno_w := ds_retorno_w || ', ' || r_c01_w.ds_motivo_cancelamento;
		end if;
		end;
	end loop;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
