SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_subestipulante (nr_seq_subestipulante_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
	DR - Data Rescisão
	DL - Data limite utilização
	N - Nome
*/
dt_rescisao_w		timestamp;
dt_limite_utilizacao_w	timestamp;
ds_retorno_w		varchar(255);
cd_cgc_w		varchar(14);
cd_pessoa_fisica_w	varchar(10);


BEGIN

select	dt_rescisao,
	dt_limite_utilizacao,
	cd_cgc,
	cd_pessoa_fisica
into STRICT	dt_rescisao_w,
	dt_limite_utilizacao_w,
	cd_cgc_w,
	cd_pessoa_fisica_w
from	pls_sub_estipulante
where	nr_sequencia	= nr_seq_subestipulante_p;

if (ie_opcao_p = 'DR') then
	ds_retorno_w := to_char(dt_rescisao_w, 'dd/mm/yyyy');
elsif (ie_opcao_p = 'DL') then
	ds_retorno_w := to_char(dt_limite_utilizacao_w, 'dd/mm/yyyy');
elsif (ie_opcao_p = 'N') then
	if (cd_cgc_w IS NOT NULL AND cd_cgc_w::text <> '') then
		select	substr(max(ds_razao_social),1,255)
		into STRICT	ds_retorno_w
		from	pessoa_juridica
		where	cd_cgc = cd_cgc_w;
	elsif (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then
		select	substr(max(nm_pessoa_fisica),1,255)
		into STRICT	ds_retorno_w
		from	pessoa_fisica
		where	cd_pessoa_fisica = cd_pessoa_fisica_w;
	end if;
end if;

return 	ds_retorno_w;

end 	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
