SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_tasy_ans ( nr_seq_tasy_ans_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* ie_opcao_p
	DA - Descrição enviar ANS
*/
ds_retorno_w		varchar(255);
ds_enviar_ans_w		varchar(100);


BEGIN

select	CASE WHEN ie_enviar_ans='I' THEN  'Inclusão por mudança de sistema' WHEN ie_enviar_ans='M' THEN  'Exclusão por mudança de sistema' WHEN ie_enviar_ans='N' THEN  'Inclusão normal' WHEN ie_enviar_ans='E' THEN  'Exclusão normal'  WHEN ie_enviar_ans='A' THEN  'Alteração cadastral' WHEN ie_enviar_ans='T' THEN  'Não enviar' END
into STRICT	ds_enviar_ans_w
from	pls_sib_tasy_ans
where	nr_sequencia	= nr_seq_tasy_ans_p;

if (ie_opcao_p = 'DS') then
	ds_retorno_w	:= ds_enviar_ans_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
