CREATE OR REPLACE FUNCTION pls_obter_dados_tipo_atend ( nr_seq_tipo_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);
ds_tipo_atendimento_w		varchar(255);
cd_tiss_w			varchar(20);


BEGIN

begin
	select	ds_tipo_atendimento,
		cd_tiss
	into STRICT	ds_tipo_atendimento_w,
		cd_tiss_w
	from	pls_tipo_atendimento
	where	nr_sequencia	= nr_seq_tipo_atendimento_p;
exception
when others then
	ds_tipo_atendimento_w := null;
	cd_tiss_w := null;
end;

if (ie_opcao_p	= 'D') then
	ds_retorno_w	:= ds_tipo_atendimento_w;
elsif (ie_opcao_p	= 'T') then
	ds_retorno_w	:= cd_tiss_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
