CREATE OR REPLACE FUNCTION pls_obter_dados_tipo_conta ( nr_seq_tipo_conta_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:   Obter dados do tipo da conta
----------------------------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ x ] Tasy (Delphi/Java) [ x ] Portal [  ] Relatórios [ ] Outros:
 ----------------------------------------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
+++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++

	IE_OPCAO_P :
	DS - Descrição do tipo da conta
*/ds_retorno_w			varchar(255) := null;


BEGIN
if (nr_seq_tipo_conta_p IS NOT NULL AND nr_seq_tipo_conta_p::text <> '') then
	if (ie_opcao_p = 'DS')then
		select	w.ds_tipo_conta ds
		into STRICT	ds_retorno_w
		from	pls_tipo_conta w
		where  	nr_sequencia 	= nr_seq_tipo_conta_p;
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
