CREATE OR REPLACE FUNCTION pls_obter_dados_tipo_reaj ( nr_seq_tipo_reajuste_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_tipo_reajuste_w	varchar(255);
ds_retorno_w		varchar(255);


BEGIN
begin
select	ds_tipo_reajuste
into STRICT	ds_tipo_reajuste_w
from	pls_tipo_reajuste
where	nr_sequencia	= nr_seq_tipo_reajuste_p;
exception
when others then
	ds_retorno_w	:= null;
end;

if (ie_opcao_p	= 'N') then
	ds_retorno_w	:= ds_tipo_reajuste_w;
end if;

return	ds_retorno_w;

end;
$body$
