SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_tit_pag_prest ( nr_seq_prestador_p bigint, dt_referencia_p timestamp, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* IE_OPCAO_P:
	T - número do título a pagar
	V - data de vencimento do título a pagar
	C - classificação DIOPS
*/
ds_retorno_w		varchar(50)	:= '';
nr_seq_pag_prest_w	pls_pagamento_prestador.nr_sequencia%type;
nr_titulo_pagar_w	varchar(50);
dt_vencimento_w		timestamp;
qt_dias_vencido_w	bigint;
dt_inicial_w		timestamp;
dt_final_w		timestamp;



BEGIN

dt_inicial_w	:= trunc(dt_referencia_p,'month');
dt_final_w	:= fim_dia(last_day(dt_referencia_p));

begin
select	max(b.nr_sequencia)
into STRICT	nr_seq_pag_prest_w
from	pls_pagamento_prestador	b,
	pls_lote_pagamento	a
where	a.nr_sequencia = b.nr_seq_lote
and	b.nr_seq_prestador = nr_seq_prestador_p
and	a.dt_mes_competencia between dt_inicial_w and dt_final_w;
exception
when others then
	nr_seq_pag_prest_w	:= null;
end;

if (nr_seq_pag_prest_w IS NOT NULL AND nr_seq_pag_prest_w::text <> '') then
	select	max(nr_titulo)
	into STRICT	nr_titulo_pagar_w
	from	pls_pag_prest_vencimento
	where	nr_seq_pag_prestador	= nr_seq_pag_prest_w;

	if (ie_opcao_p = 'T') then
		ds_retorno_w 	:= nr_titulo_pagar_w;
	elsif (ie_opcao_p in ('V','C')) then
		select	dt_vencimento_atual
		into STRICT	dt_vencimento_w
		from	titulo_pagar
		where	nr_titulo = nr_titulo_pagar_w;

		if (ie_opcao_p = 'V') then
			ds_retorno_w := dt_vencimento_w;
		elsif (ie_opcao_p = 'C') then
			if (dt_vencimento_w > clock_timestamp()) then
				ds_retorno_w := Wheb_mensagem_pck.get_texto(300065);
			else
				qt_dias_vencido_w := dt_vencimento_w - clock_timestamp();
				if (qt_dias_vencido_w <= 30) then
					ds_retorno_w := Wheb_mensagem_pck.get_texto(300059);
				elsif (qt_dias_vencido_w <= 60) then
					ds_retorno_w := Wheb_mensagem_pck.get_texto(300060);
				elsif (qt_dias_vencido_w <= 90) then
					ds_retorno_w := Wheb_mensagem_pck.get_texto(300061);
				elsif (qt_dias_vencido_w <= 120) then
					ds_retorno_w := Wheb_mensagem_pck.get_texto(300062);
				else
					ds_retorno_w := Wheb_mensagem_pck.get_texto(300064);
				end if;
			end if;
		end if;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_dados_tit_pag_prest ( nr_seq_prestador_p bigint, dt_referencia_p timestamp, ie_opcao_p text) FROM PUBLIC;

