SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_tp_hist_prest ( nr_seq_tipo_hist_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*	D - Descrição
	E - Exibe prestador
*/
ds_retorno_w			varchar(255);
ds_tipo_hist_w			varchar(255);
ie_wxibe_w			varchar(20);


BEGIN

select	ds_tipo,
	CASE WHEN ie_exibe_prestador='S' THEN 'Sim'  ELSE 'Não' END
into STRICT	ds_tipo_hist_w,
	ie_wxibe_w
from	pls_tipo_hist_prestador
where	nr_sequencia	= nr_seq_tipo_hist_p;

if (ie_opcao_p	= 'D') then
	ds_retorno_w	:= ds_tipo_hist_w;
elsif (ie_opcao_p	= 'E') then
	ds_retorno_w	:= ie_wxibe_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
