CREATE OR REPLACE FUNCTION pls_obter_dados_ult_impug_def (nr_seq_impugnacao_p pls_impugnacao.nr_sequencia%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w varchar(2000);
/*
	ie_opcao_p
	'ST' = Status
*/
BEGIN
ds_retorno_w := '';

if (ie_opcao_p = 'ST') then
	select w.ie_status
	into STRICT	ds_retorno_w
	from (SELECT	ie_status
		from	pls_impugnacao_defesa
		where	nr_seq_impugnacao = nr_seq_impugnacao_p
		and	coalesce(dt_cancelamento::text, '') = ''
		order by dt_defesa desc)	w LIMIT 1;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
