SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dados_valor_fatura (nr_seq_fatura_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE

 
ie_opcao_w		varchar(10);
nr_seq_prestador_w	bigint;
nr_seq_conta_w		bigint;
ie_tipo_guia_w		varchar(2);
ie_tipo_relacao_w	varchar(2);
vl_item_w		double precision := 0;
vl_retorno_w		double precision := 0;

--	IE_OPCAO 
-- CHMC	- Consultas e honorários médicos cooperados 
-- CHMN	- Consultas e honorários médicos contratados 
-- ETC	- Exames e terapias cooperados 
-- ETN	- Exames e terapias contratados 
-- ETR	- Exames e terapias rede própria 
-- IRP	- Internações Rede própria 
-- IRC	- Internações Rede contratada 
 
C01 CURSOR FOR 
	SELECT	e.nr_sequencia 
	from	pls_conta		e, 
		pls_fatura_conta	c, 
		pls_fatura_evento	b, 
		pls_fatura		a 
	where	a.nr_sequencia	= b.nr_seq_fatura 
	and	b.nr_sequencia	= c.nr_seq_fatura_evento 
	and	e.nr_sequencia	= c.nr_seq_conta 
	and	a.nr_sequencia	= nr_seq_fatura_p 
	group by 
		e.nr_sequencia;
	

BEGIN 
ie_opcao_w := upper(ie_opcao_p);
 
open C01;
loop 
fetch C01 into 
	nr_seq_conta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	begin 
	select	ie_tipo_guia, 
		nr_seq_prestador_exec 
	into STRICT	ie_tipo_guia_w, 
		nr_seq_prestador_w 
	from	pls_conta 
	where	nr_sequencia = nr_seq_conta_w;
	exception 
	when others then 
		ie_tipo_guia_w := null;
		nr_seq_prestador_w := null;
	end;
	 
	-- IE_TIPO_GUIA_W - Dominio 1746 
	-- 3        Guia de consulta 
	-- 4        Guia de SP/SADT 
	-- 5        Guia de Resumo de Internação 
	-- 6        Guia de Honorário Individual 
	 
	if (nr_seq_prestador_w IS NOT NULL AND nr_seq_prestador_w::text <> '') then 
		begin 
		select	ie_tipo_relacao 
		into STRICT	ie_tipo_relacao_w 
		from	pls_prestador 
		where	nr_sequencia = nr_seq_prestador_w;
		exception 
		when others then 
			ie_tipo_relacao_w := null;
		end;
	end if;
	 
	-- IE_TIPO_RELACAO_W - Dominio 1668 
	-- C        Cooperado 
	-- D       Rede contratualizada direta 
	-- F        Fornecedor 
	-- I        Rede contratualizada indireta 
	-- P        Rede própria 
	 
	if (ie_tipo_guia_w IS NOT NULL AND ie_tipo_guia_w::text <> '') and (ie_tipo_relacao_w IS NOT NULL AND ie_tipo_relacao_w::text <> '') then 
		 
		if (ie_opcao_w = 'CHMC') then 
			if (ie_tipo_guia_w in ('3','6')) and (ie_tipo_relacao_w = 'C') then 
				select	sum(b.vl_beneficiario) 
				into STRICT	vl_item_w 
				from	pls_conta_pos_estabelecido	b, 
					pls_conta_proc	a 
				where	a.nr_sequencia	= b.nr_seq_conta_proc 
				and	a.nr_seq_conta	= nr_seq_conta_w;
			end if;
		 
		elsif (ie_opcao_w = 'CHMN') then 
			if (ie_tipo_guia_w in ('3','6')) and (ie_tipo_relacao_w in ('F','D','I')) then 
				select	sum(b.vl_beneficiario) 
				into STRICT	vl_item_w 
				from	pls_conta_pos_estabelecido	b, 
					pls_conta_proc	a 
				where	a.nr_sequencia	= b.nr_seq_conta_proc 
				and	a.nr_seq_conta	= nr_seq_conta_w;
			end if;
		 
		elsif (ie_opcao_w = 'ETC') then 
			if (ie_tipo_guia_w = '4') and (ie_tipo_relacao_w = 'C') then 
				select	sum(b.vl_beneficiario) 
				into STRICT	vl_item_w 
				from	pls_conta_pos_estabelecido	b, 
					pls_conta_proc	a 
				where	a.nr_sequencia	= b.nr_seq_conta_proc 
				and	a.nr_seq_conta	= nr_seq_conta_w;
			end if;
		 
		elsif (ie_opcao_w = 'ETN') then 
			if (ie_tipo_guia_w = '4') and (ie_tipo_relacao_w in ('F','D','I')) then 
				select	sum(b.vl_beneficiario) 
				into STRICT	vl_item_w 
				from	pls_conta_pos_estabelecido	b, 
					pls_conta_proc	a 
				where	a.nr_sequencia	= b.nr_seq_conta_proc 
				and	a.nr_seq_conta	= nr_seq_conta_w;
			end if;
	 
		elsif (ie_opcao_w = 'ETR') then 
			if (ie_tipo_guia_w = '4') and (ie_tipo_relacao_w = 'P') then 
				select	sum(b.vl_beneficiario) 
				into STRICT	vl_item_w 
				from	pls_conta_pos_estabelecido	b, 
					pls_conta_proc	a 
				where	a.nr_sequencia	= b.nr_seq_conta_proc 
				and	a.nr_seq_conta	= nr_seq_conta_w;
			end if;
	 
		elsif (ie_opcao_w = 'IRP') then 
			if (ie_tipo_guia_w = '5') and (ie_tipo_relacao_w = 'P') then 
				select	sum(b.vl_beneficiario) 
				into STRICT	vl_item_w 
				from	pls_conta_pos_estabelecido	b, 
					pls_conta_proc	a 
				where	a.nr_sequencia	= b.nr_seq_conta_proc 
				and	a.nr_seq_conta	= nr_seq_conta_w;
			end if;
		 
		elsif (ie_opcao_w = 'IRC') then 
			if (ie_tipo_guia_w = '5') and (ie_tipo_relacao_w in ('F','D','I')) then 
				select	sum(b.vl_beneficiario) 
				into STRICT	vl_item_w 
				from	pls_conta_pos_estabelecido	b, 
					pls_conta_proc	a 
				where	a.nr_sequencia	= b.nr_seq_conta_proc 
				and	a.nr_seq_conta	= nr_seq_conta_w;
			end if;
		end if;
		 
		vl_retorno_w := vl_retorno_w + vl_item_w;
	end if;
	end;
end loop;
close C01;
 
return	vl_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_dados_valor_fatura (nr_seq_fatura_p bigint, ie_opcao_p text) FROM PUBLIC;

